INSERT INTO `t_hospital` VALUES (100033, NULL, NULL, 1005, 1009, '北京回龙观医院', 'Beijing HuiLongGuan Hospital ', '北京回龙观医院是北京市卫生健康委、北京市医院管理局直属的精神卫生专科医疗机构。医院占地面积14.7万平方米，总建筑面积约6.5万平方米，编制床位1369张，职工1100余人，配有西门子顶级Prisma3.0T磁共振，GE64排全身螺旋CT、红外线热功能成像仪、生化免疫流水线以及血液分析流水线等大型医疗设备，是北京市乃至全国规模最大的公立三级甲等精神卫生专科医院，北京市医疗保险A类定点医疗机构。\n       医院是北京大学回龙观临床医学院，北京心理危机研究与干预中心，世界卫生组织心理危机预防研究与培训合作中心；是首批国家精神病临床重点专科单位，首批国家级精神科住院医师规范化培训基地，国家药物临床试验机构，国家中医药管理局\"十二五\"神志病重点专科建设单位；是中国音乐治疗学会、中国心理卫生协会残疾人心理卫生分会和中国康复协会精神残疾康复专业委员会的主任委员单位。\n医院位于北京市昌平区回龙观，始建于1958年，自1986年起独立建院。医院始终坚持“把患者服务好、把学科发展好、把人才培养好、把职工保障好、把医院建设好”的使命，始终坚定“打造一所国内领先、国际知名的现代化精神卫生专业机构”的目标，以强劲的生命力和蓬勃的创造力快速发展，成为一所综合实力卓越、亚专科齐备、学科特色鲜明、享誉国内外的精神卫生专业医疗机构。', '', '(010)83024000', '(010)62716285', 'bjhlgyy@163.com', 'http://www.bhlgh.com/', '北京回龙观医院', '10100000', '10110000', '10110000', '北京昌平回龙观', '', '100096', 0, 0, '{\"ethicsData\":{\"cellStyleMap\":{},\"contacts\":[],\"key1\":\"010-62715511转6390\"},\"geneticsOfficeData\":{\"cellStyleMap\":{},\"contacts\":[]},\"instructionData\":{\"cellStyleMap\":{},\"contacts\":[{\"cellStyleMap\":{},\"name\":\"谭云龙\",\"title\":\"副院长\"},{\"cellStyleMap\":{},\"name\":\"李娟\",\"title\":\"机构办副主任\"},{\"cellStyleMap\":{},\"name\":\"杨甫德\",\"title\":\"机构主任\"},{\"cellStyleMap\":{},\"email\":\"asongsi@163.com，amongsi@hotmail.com\",\"name\":\"陈松\",\"title\":\"机构办秘书\"}],\"key46\":\"北京昌平回龙观，京藏高速东侧辅路西三旗桥北。\",\"key47\":\"010-62712052\",\"key49\":\"010-83024477\"},\"pharmacyData\":{\"cellStyleMap\":{}},\"smoAndCroPlan\":{\"cellStyleMap\":{},\"smoAndCroList\":[]}}', NULL, NULL, 1, 1564022096507, 10010, 1584673790563, 0, '', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `t_hospital` VALUES (100034, NULL, NULL, 1001, 1009, '北京积水潭医院', 'Beijing Jishuitan Hospital', '北京积水潭医院以显著的医疗特色和医、教、研、防综合实力成为北京大学第四临床医学院。其脊柱外科、创伤骨科、矫形骨科、手外科、小儿骨科、骨肿瘤科、运动医学科和烧伤科的医疗技术达到世界及国内领先水平。医院设有北京市创伤骨科研究所、北京市烧伤研究所、北京市手外科研究所、北京创伤烧伤抢救中心、北京市骨科疾病研究治疗中心，全国计算机辅助外科学会和计算机辅助外科研究和应用中心也设在该院。\n', '', '010-58516688', '010-58516095', NULL, 'http://www.jst-hosp.com.cn/', NULL, '10100000', '10110000', '10110000', '北京市西城区新街口东街31号', NULL, '100035', 0, 0, '{\"ethicsData\":{\"cellStyleMap\":{},\"contacts\":[{\"cellStyleMap\":{},\"name\":\"蒋协远\",\"telePhone\":\"58516158\",\"title\":\"主任委员\"},{\"cellStyleMap\":{},\"name\":\"贺良\",\"telePhone\":\"58516158\",\"title\":\"副主任委员\"},{\"cellStyleMap\":{},\"name\":\"马玉华\",\"telePhone\":\"58516158\",\"title\":\"副主任委员\"}],\"key1\":\"010-58516158\",\"key10\":\"每月召开1次常规审查会议\",\"key5\":\"新街口东街31号院2\",\"key7\":\"http://www.jst-hosp.com.cn/Html/News/Columns/673/Index.html\",\"key9\":\"每月的第2周或第3周\"},\"geneticsOfficeData\":{\"cellStyleMap\":{},\"contacts\":[]},\"instructionData\":{\"cellStyleMap\":{},\"contacts\":[{\"cellStyleMap\":{},\"email\":\"Chen737@163.com\",\"name\":\"陈志刚\",\"telePhone\":\"010-58516890\",\"title\":\"机构办公室主任\"},{\"cellStyleMap\":{},\"email\":\"cuiyanzheng_@163.com\",\"name\":\"崔岩峥\",\"telePhone\":\"010-58516884\",\"title\":\"机构秘书\"}],\"key1\":\"http://www.jst-hosp.com.cn/Html/News/Articles/107685.html\",\"key4\":\"周一至周五\",\"key46\":\"旧北楼4楼 gcp中心\",\"key49\":\"010-58516890；010-58516884\"},\"pharmacyData\":{\"cellStyleMap\":{}},\"smoAndCroPlan\":{\"cellStyleMap\":{},\"smoAndCroList\":[]}}', NULL, NULL, 1, 1564022096534, 10010, 1584673796833, 0, '', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
