INSERT INTO `t_hospital` VALUES (100053, NULL, NULL, 1001, 1009, '常德市第一人民医院', 'The First People\'s Hosptial of changde city', '常德市第一人民医院创建于1898年，原名广济诊所，1915年更名为广德医院，是由美国教会创办的湖南省首家西医医院。相继更名为常德专区第一人民医院、省立常德医院、常德人民医院、常德地区人民医院、常德市第一人民医院，现已成为一所集医疗、科研、教学、预防、保健、康复等功能为一体的大型综合性三级甲等医院。', '', '86-0736-7788875', NULL, NULL, 'http://yy.changde.gov.cn/index.html', NULL, '10100000', '10430000', '10430700', '湖南省常德市人民路818号', NULL, NULL, 0, 0, '{\"ethicsData\":{\"cellStyleMap\":{},\"contacts\":[{\"cellStyleMap\":{},\"name\":\"邓志明\",\"title\":\"主任委员\"},{\"cellStyleMap\":{},\"email\":\"450717196@qq.com\",\"name\":\"周述香\",\"title\":\"伦理委员会秘书\"}],\"key1\":\"0736-7788890\",\"key5\":\"湖南省常德市武陵区人民路818号医院第二住院部——第三住院楼连楼2楼\",\"key8\":\"周一至周五\"},\"geneticsOfficeData\":{\"cellStyleMap\":{},\"contacts\":[]},\"instructionData\":{\"cellStyleMap\":{},\"contacts\":[],\"key4\":\"周一至周六（上午8:00-12:00，下午2:30-5:30）\",\"key46\":\"常德市武陵区人民路818号 常德市第一人民医院第一住院部2楼\",\"key47\":\"0736-7788406\",\"key49\":\"0736-7788406\"},\"pharmacyData\":{\"cellStyleMap\":{}},\"smoAndCroPlan\":{\"cellStyleMap\":{},\"smoAndCroList\":[]}}', NULL, NULL, 1, 1564022097174, 10010, 1583919227015, 0, '', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
