INSERT INTO `t_hospital` VALUES (100084, NULL, NULL, 1001, 1009, '大庆市人民医院', 'Daqing People\'s Hospital', ' 大庆市人民医院（哈尔滨医科大学附属第五医院、哈尔滨医科大学第五临床医学院、中国人民解放军预备役82190部队野战医院）是一所功能完善、设备齐全、环境优美、技术先进，集医疗、教学、科研、预防保健、康复为一体的三级甲等综合医院。医院成立于2000年，2005年成为哈尔滨医科大学附属第五医院、2010年通过了三级甲等综合性医院评审，2011年成为哈尔滨医科大学第五临床医学院。\n        医院现有南北两个院区，占地面积35万平方米，设置床位1700张，开放床位1277张，设有医疗、医技等专业科室73个，是国家卫计委认定的全国首批国家级住院医师规范化培训基地、助理全科医师规范化培训基地、全科医师转岗培训基地。\n        医院技术力量雄厚、人才济济、设备先进、信誉一流，是全国网络心电会诊分中心、卫生部病理远程会诊与质控试点单位、中国卒中联盟大庆分中心、北京空军总医院中西医结合正骨治疗分中心、大庆市危重孕产妇救治中心、大庆市乳腺病诊疗中心、大庆市病理质量控制中心。儿科学为省级学科梯队，普外科学、骨科学等4个学科为市级重点学科，心内科、神经内科等19个专科为市级重点专科。\n        现有职工2207人，副高职以上人员638人，其中教授15人，副教授45人，博士13人，硕士236人，硕士研究生导师9人；拥有640层CT、3.0T核磁、四维彩超、DSA等大型医疗设备，总价值2.68亿元，总资产达6.03亿元。\n        医院以“崇德尽术、践信仰和”为院训，靠质量树信誉、靠服务创品牌、靠管理出效益、靠创新谋发展。曾获得全国卫生系统先进集体、全国首批“百姓放心示范医院”、“全国诚信建设示范单位”、“黑龙江省五一劳动奖状”和大庆市“‘关注民生服务发展’群众最满意医院”等荣誉，连续多年获得黑龙江省“行风建设最佳单位”殊荣。', '', '0459-6612000', NULL, NULL, 'https://www.dqsrmyy.com/', NULL, '10100000', '10230000', '10230600', '黑龙江省大庆市开发区建设路241号', NULL, NULL, 0, 0, '{\"ethicsData\":{\"cellStyleMap\":{},\"contacts\":[{\"cellStyleMap\":{},\"name\":\"赵法章\",\"title\":\"主任\"},{\"cellStyleMap\":{},\"email\":\"1165462651@qq.com\",\"name\":\"赵琪\",\"title\":\"秘书\"}],\"key5\":\"大庆市人民医院门诊一楼\",\"key8\":\"周一至周五，上午8:00-11:30，下午13:00-16:30\"},\"geneticsOfficeData\":{\"cellStyleMap\":{},\"contacts\":[]},\"instructionData\":{\"cellStyleMap\":{},\"contacts\":[{\"cellStyleMap\":{},\"name\":\"班润武\",\"title\":\"机构负责人\"},{\"cellStyleMap\":{},\"email\":\"Jxp626@163.com\",\"name\":\"姜雪萍\",\"telePhone\":\"0459-6612860\",\"title\":\"机构办公室主任\"},{\"cellStyleMap\":{},\"email\":\"18504591989@163.com\",\"name\":\"李欣欣\",\"telePhone\":\"0459-6612898\",\"title\":\"机构秘书&资料管理员\"},{\"cellStyleMap\":{},\"email\":\"pxj234612@163.com\",\"name\":\"朴仙姬\",\"telePhone\":\"0459-6612898\",\"title\":\"质控员\"},{\"cellStyleMap\":{},\"name\":\"张艳梅\",\"title\":\"药物管理员\"},{\"cellStyleMap\":{},\"name\":\"张琨\",\"title\":\"药物管理员\"}],\"key46\":\"大庆市人民医院门诊一楼\",\"key47\":\"0459-6612898\",\"key49\":\"0459-6612898;0459-6612860\",\"key50\":\"dqsrmyygcp@163.com\"},\"pharmacyData\":{\"cellStyleMap\":{}},\"smoAndCroPlan\":{\"cellStyleMap\":{},\"smoAndCroList\":[]}}', NULL, NULL, 1, 1564022098019, 10010, 1583919178179, 0, '', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
