INSERT INTO `t_hospital` VALUES (100127, NULL, NULL, 1001, 1009, '赣南医学院第一附属医院', NULL, '赣南医学院第一附属医院有两个院区，章贡院区位于江西省赣州市章贡区青年路23号，始建于1958年，占地面积208亩，是一所集医疗、教学、科研、预防于一体的三级甲等综合性医院，是赣州市医保和新农合定点医院。', '', '0797-8266006，8266059', NULL, NULL, 'http://www.gyyfy.com/', NULL, '10100000', '10360000', '10360700', '江西省赣州市章贡区青年路23号', '', NULL, 0, 0, '{\"ethicsData\":{\"cellStyleMap\":{},\"contacts\":[{\"cellStyleMap\":{},\"email\":\"gnxxj6709@163.com\",\"name\":\"徐小军\",\"position\":\"伦理主任\"},{\"cellStyleMap\":{},\"email\":\"gyfyllwyh@163.com\",\"mobilePhone\":\"18870701616\",\"name\":\"方奕巍\",\"position\":\"伦理秘书\"}],\"key1\":\"0797-8689370\",\"key3\":\"gyfyllwyh@163.com\",\"key5\":\"赣州市青年路23号赣南医学院第一附属医院章贡院区1号住院楼1楼\",\"key8\":\"工作日\"},\"geneticsOfficeData\":{\"cellStyleMap\":{},\"contacts\":[]},\"instructionData\":{\"cellStyleMap\":{},\"contacts\":[{\"cellStyleMap\":{},\"email\":\"zgx8778@163.com\",\"name\":\"张国玺\",\"position\":\"机构主任\"},{\"cellStyleMap\":{},\"email\":\"gzyanjinling@163.com\",\"name\":\"严金玲\",\"position\":\"机构办主任\"},{\"cellStyleMap\":{},\"email\":\"zxiaojuan8810@126.com\",\"name\":\"钟小娟\",\"position\":\"机构秘书\"},{\"cellStyleMap\":{},\"email\":\"belinda0512@163.com\",\"name\":\"李蓓\",\"position\":\"质控员\"},{\"cellStyleMap\":{},\"email\":\"944849774@qq.com\",\"name\":\"李斌\",\"position\":\"药物管理员\"},{\"cellStyleMap\":{},\"email\":\"84558571@qq.com\",\"name\":\"李方方\",\"position\":\"资料管理员\"},{\"cellStyleMap\":{},\"email\":\"gaochang1989.ok@163.com\",\"name\":\"高畅\",\"position\":\"信息管理员\"}],\"key4\":\"工作日\",\"key46\":\"赣州市青年路23号赣南医学院第一附属医院章贡院区2号住院楼2楼\",\"key49\":\"0797-8689369\",\"key50\":\"gyyfygcp@163.com\"},\"pharmacyData\":{\"cellStyleMap\":{}},\"smoAndCroPlan\":{\"cellStyleMap\":{},\"smoAndCroList\":[]}}', NULL, NULL, 1, 1564022099214, 10010, 1583919283249, 0, '', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `t_hospital` VALUES (100128, NULL, NULL, 1001, 1009, '赣州市人民医院', NULL, '赣州市人民医院始建于1939年7月，位于有着江南宋城、客家摇篮、红色故都、世界橙乡美誉的赣州中心城区，现已形成“一院两区”格局，集医疗、教学、科研、保健、康复于一体，是南昌大学附属医院，国家支持的区域性医疗中心，江西省首家第三周期三级甲等综合医院，赣州市规模最大、技术力量最雄厚、医疗设备最精良、管理及服务理念最先进的现代化医院。业务总量、综合服务能力位居全省第三、地市级医院第一，牢固确立了在全省地市级医院中的领军地位。', '', '0797-6120120（8小时内） 0797-5889023（8小时外）', NULL, NULL, 'http://www.gzsrmyy.com/', NULL, '10100000', '10360000', '10360700', '赣州市章贡区梅关大道16号', '', NULL, 0, 0, '{\"ethicsData\":{\"cellStyleMap\":{},\"contacts\":[],\"key1\":\"0797-5889157\",\"key3\":\"gzsrmyyywlcsyllwyh@163.com\",\"key5\":\"江西省赣州市章贡区梅关大道16号赣州市人民医院行政楼二楼201房\"},\"geneticsOfficeData\":{\"cellStyleMap\":{},\"contacts\":[]},\"instructionData\":{\"cellStyleMap\":{},\"contacts\":[],\"key46\":\"江西省赣州市章贡区梅关大道16号赣州市人民医院行政楼203\",\"key49\":\"0797-5889198\",\"key50\":\"gzsrmyygcp@163.com\"},\"pharmacyData\":{\"cellStyleMap\":{}},\"smoAndCroPlan\":{\"cellStyleMap\":{},\"smoAndCroList\":[]}}', NULL, NULL, 1, 1564022099242, 10010, 1583919284814, 0, '', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `t_hospital` VALUES (100129, NULL, NULL, 1005, 1009, '赣州市肿瘤医院', NULL, '赣州市肿瘤医院（赣南医学院附属肿瘤医院 赣州市肿瘤防治研究所）是赣南及周边地区唯一一所集医疗、教学、科研、预防为一体的临床研究型三级甲等肿瘤专科医院。\n\n医院位于赣州市章贡区水东镇万松山下，占地面积80000平方米，医疗用房面积4万多平方米，现有床位约600张。现有职工500余人，1人享受国务院津贴，1人享受市政府津贴。', '', '0797-8109362', '0797-8187639', NULL, 'http://www.gzzlyy.com/', NULL, '10100000', '10360000', '10360700', '江西省赣州市章贡区水东花园前19号', '', NULL, 0, 0, '{\"ethicsData\":{\"cellStyleMap\":{},\"contacts\":[],\"key1\":\"0797-5166652\",\"key3\":\"zlyyylw@163.com\",\"key5\":\"江西省赣州市章贡区水东镇花园前19号2号楼5楼伦理委员会办公室\"},\"geneticsOfficeData\":{\"cellStyleMap\":{},\"contacts\":[]},\"instructionData\":{\"cellStyleMap\":{},\"contacts\":[{\"cellStyleMap\":{},\"mobilePhone\":\"18720876527\",\"name\":\"徐青云\",\"position\":\"机构秘书\"}],\"key46\":\"江西省赣州市章贡区水东镇花园前19号 赣州市肿瘤医院 2号住院大楼5楼\",\"key47\":\"0797-5162678\",\"key49\":\"0797-5162678\",\"key50\":\"zszlyygcp@163.com\"},\"pharmacyData\":{\"cellStyleMap\":{}},\"smoAndCroPlan\":{\"cellStyleMap\":{},\"smoAndCroList\":[]}}', NULL, NULL, 1, 1564022099270, 10010, 1583919284738, 0, '', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
