INSERT INTO `t_hospital` VALUES (100144, NULL, NULL, 1005, 1009, '广州市妇女儿童医疗中心', 'Guangdong women and children\'s medical hospital', '妇儿中心是华南地区规模最大的三级甲等妇女儿童医院，是儿科学硕士、博士学位授予点。目前拥有国家临床重点专科3个（小儿外科，小儿呼吸，小儿消化），广东省儿童健康与疾病临床医学研究中心，广东省结构性出生缺陷疾病研究重点实验室，广东省国家基因检测技术应用示范中心遗传性疾病应用分中心，广东省妇幼健康医疗数据智能化应用工程技术研究中心，广东省儿童早期发展应用工程技术研究中心，广东省临床重点专科（学科）6个；拥有国际标准的百万级生物样本库，开发了国际先进的具有自主知识产权的出生队列信息管理系统，建立了全世界规模最大之一的妇幼队列研究平台。2019年1月，成功入选广东省高水平医院第二批重点建设医院，获得政府3亿元的资金支持。是国家级儿童早期发展示范基地，全国“三八”红旗单位。', '', '020-81886332', NULL, NULL, 'http://www.gzfezx.com/', NULL, '10100000', '10440000', '10440100', '广州市金穗路9号', '', '510623', 0, 0, '{\"ethicsData\":{\"cellStyleMap\":{},\"contacts\":[{\"cellStyleMap\":{},\"name\":\"王洪涛\",\"position\":\"伦理主任\"},{\"cellStyleMap\":{},\"email\":\"gzfezxgcplunli@163.com\",\"name\":\"肖楚瑶\",\"position\":\"伦理秘书\",\"telePhone\":\"020-38857707\"}],\"key1\":\"020-38857707\",\"key10\":\"一个半月一次\",\"key17\":\"3000\",\"key3\":\"gzfezxgcplunli@163.com\",\"key5\":\"广州市天河区珠江新城华强路9号保利克洛维1804室\",\"key9\":\"不固定\"},\"geneticsOfficeData\":{\"cellStyleMap\":{},\"contacts\":[]},\"instructionData\":{\"cellStyleMap\":{},\"contacts\":[{\"cellStyleMap\":{},\"email\":\"fezxgcp@163.com\",\"name\":\"夏慧敏\",\"position\":\"机构主任\",\"telePhone\":\"020-38857707\"},{\"cellStyleMap\":{},\"name\":\"冯琼\",\"position\":\"机构副主任\",\"telePhone\":\"020-38076262\"},{\"cellStyleMap\":{},\"name\":\"何艳玲\",\"position\":\"机构办公室主任\"},{\"cellStyleMap\":{},\"name\":\"和凡\",\"position\":\"机构秘书\",\"telePhone\":\"020-38857706\"},{\"cellStyleMap\":{},\"name\":\"肖楚瑶\",\"position\":\"质控员\"},{\"cellStyleMap\":{},\"name\":\"王穗莹\",\"position\":\"药房药品管理员\"},{\"cellStyleMap\":{},\"name\":\"岑菡婧\",\"position\":\"资料管理员\"}],\"key1\":\"http://www.gzfezx.com/yuanwugongkai/jyfw/ksjs/ywlcsy/\",\"key10\":\"是\",\"key11\":\"是\",\"key4\":\"周一至周五8-12点，14-17点\",\"key46\":\"广州市天河区珠江新城华强路9号保利克洛维1804室\",\"key49\":\"020-38857707\",\"key50\":\"fezxgcp@163.com\",\"key6\":\"是\"},\"pharmacyData\":{\"cellStyleMap\":{}},\"smoAndCroPlan\":{\"cellStyleMap\":{},\"s