INSERT INTO `t_hospital` VALUES (100164, NULL, NULL, 1001, 1009, '国药东风总医院', NULL, '国药东风总医院，中国医药集团辖属医院，暨湖北医药学院附属东风医院，位于中国卡车之都、道教圣地、南水北调中线工程起源地湖北省十堰市。自1927年建院至今，已走过90余年发展历程，扎根十堰50年，是鄂西北地区首家集医疗、教学、科研、防保、康复、急救为一体的大型综合性国家三级甲等医院，先后荣获全国百佳医院、全国百姓放心示范医院、全国文明单位等荣誉称号。', '', '0719-8210666', NULL, NULL, 'https://www.dfmhp.com.cn/', NULL, '10100000', '10420000', '10420300', '十堰市张湾区大岭路16号', '', NULL, 0, 0, '{\"ethicsData\":{\"cellStyleMap\":{},\"contacts\":[]},\"geneticsOfficeData\":{\"cellStyleMap\":{},\"contacts\":[]},\"instructionData\":{\"cellStyleMap\":{},\"contacts\":[],\"key46\":\"湖北十堰市大岭路16号\",\"key49\":\"13581373699\"},\"pharmacyData\":{\"cellStyleMap\":{}},\"smoAndCroPlan\":{\"cellStyleMap\":{},\"smoAndCroList\":[]}}', NULL, NULL, 1, 1564022100402, 10010, 1583919200333, 0, '', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
