INSERT INTO `t_hospital` VALUES (100187, NULL, NULL, 1001, 1009, '杭州康柏医院', NULL, '康柏医院的选址位于杭州市西湖区转塘街道大清村1号，总建筑面积为13800方，医院和灵隐寺及梅家坞景区仅只有一山之隔，石砌沿山游步道已联通西湖周边各风景点，而就在拟建医院周边就有环境优美，风景如画的公园。 康柏医院位于大清谷风景区，是一家以康复治疗和临床研究为主要发展方向的综合性医院。医院设有1000余平方米康复治疗科，拥有国内先进的康复治疗设备，针对神经康复、骨关节康复及老年康复患者功能障碍特点制定个性化康复治疗方案，疗效可靠。', '', '', NULL, NULL, '', NULL, '10100000', '10330000', '10330100', '浙江杭州西湖区转塘街道大清村1号（杭州康柏医院）', NULL, NULL, 0, 0, '{\"ethicsData\":{\"cellStyleMap\":{},\"contacts\":[]},\"geneticsOfficeData\":{\"cellStyleMap\":{},\"contacts\":[]},\"instructionData\":{\"cellStyleMap\":{},\"contacts\":[{\"cellStyleMap\":{},\"name\":\"陈建\",\"position\":\"机构办主任\",\"telePhone\":\"0571-56109970\"},{\"cellStyleMap\":{},\"name\":\"赵丹丹\",\"position\":\"机构秘书\",\"telePhone\":\"0571-56109972\"},{\"cellStyleMap\":{},\"name\":\"朱怡\",\"position\":\"药物管理员\",\"telePhone\":\"0571-56109972\"}],\"key4\":\"周一~周五\",\"key46\":\"浙江省杭州市西湖区清谷路大清村1号\",\"key47\":\"0571-56109972\",\"key49\":\"0571-56109970/56109974\",\"key50\":\"jian.chen@combak.cn\"},\"pharmacyData\":{\"cellStyleMap\":{}},\"smoAndCroPlan\":{\"cellStyleMap\":{},\"smoAndCroList\":[]}}', NULL, NULL, 1, 1564022101058, 10010, 1583919472602, 0, '', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `t_hospital` VALUES (100188, NULL, NULL, 1001, 1009, '杭州师范大学附属医院', 'The Affiliated Hospital of Hangzhou Normal Nniversity', '建于1949年的杭州师范大学附属医院是一所融医疗、教学、科研、预防保健为一体的浙江省三级甲等综合医院，是国务院学位委员会批准的临床医学硕士专业学位授权点、国家药物临床试验机构、首批国家住院医师规范化培训基地、中国胸痛中心示范基地及浙江省博士后工作单位，是杭州市委市政府与德中卫生组织确定的浙江省首家共建“中德（杭州）国际医院”。', '', '86-0571-88015050', NULL, NULL, 'http://www.hz2y.com/', NULL, '10100000', '10330000', '10330100', '杭州市温州路126号', '', NULL, 0, 0, '{\"ethicsData\":{\"cellStyleMap\":{},\"contacts\":[]},\"geneticsOfficeData\":{\"cellStyleMap\":{},\"contacts\":[]},\"instructionData\":{\"cellStyleMap\":{},\"contacts\":[],\"key46\":\"浙江省杭州市温州路126号\",\"key49\":\"0571-88358436\"},\"pharmacyData\":{\"cellStyleMap\":{}},\"smoAndCroPlan\":{\"cellStyleMap\":{},\"smoAndCroList\":[]}}', NULL, NULL, 1, 1564022101086, 10010, 1583919488059, 0, '', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
