INSERT INTO `t_hospital` VALUES (100190, NULL, NULL, 1001, 1010, '杭州市第三人民医院', 'The Third People\'s Hospital of Hangzhou', '杭州市第三人民医院创建于1950年，是一家市属三级乙等综合性医院，为浙江中医药大学附属医院和安徽医科大学杭州临床学院、安徽医科大学研究生和浙江中医药大学硕士研究生培养基地，是\"全国精神文明建设工作先进单位\"、\"全国百姓放心示范医院\"、\"浙江省文明单位\"、\"浙江省级绿色医院\"、首届\"我心目中的杭州品牌”。2012年，医院皮肤科成为全国临床重点专科建设项目', '', '87823107', NULL, NULL, 'http://www.hz3yy.com/', NULL, '10100000', '10330000', '10330100', '杭州市西湖大道38号', 'No. 38 West Lake Road, Hangzhou', NULL, 0, 0, '{\"ethicsData\":{\"cellStyleMap\":{},\"contacts\":[],\"key1\":\"0571-87827232\",\"key3\":\"hzsyec@126.com\"},\"geneticsOfficeData\":{\"cellStyleMap\":{},\"contacts\":[]},\"instructionData\":{\"cellStyleMap\":{},\"contacts\":[{\"cellStyleMap\":{},\"mobilePhone\":\"18658812533\",\"name\":\"周老师\"},{\"cellStyleMap\":{},\"name\":\"吕老师\"}],\"key46\":\"浙江省杭州市上城区西湖大道38号杭州市第三人民医院门诊楼1701室\\n\",\"key47\":\"0571-87823189\",\"key49\":\"0571-87823189\",\"key50\":\"hz3ygcp@163.com，drzjh@163.com\"},\"pharmacyData\":{\"cellStyleMap\":{}},\"smoAndCroPlan\":{\"cellStyleMap\":{},\"smoAndCroList\":[]}}', NULL, NULL, 1, 1564022101137, 10010, 1583919479212, 0, '', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `t_hospital` VALUES (100191, NULL, NULL, 1001, 1009, '杭州市第一人民医院', NULL, '医院创建于1923年，是杭州地区融医疗、教学、科研、预防和社会保健于一体的市属最大的综合性三级甲等医院，是浙江省首批通过三甲评审的四家医院之一，现为 “浙江大学医学院附属杭州市第一人民医院“、“浙江中医药大学第四临床医学院”、杭州市消化病医院。医院注重恶性肿瘤多学科治疗、心脑血管疾病诊治、危重孕产妇救治、健康管理等学科群建设。承担杭州市肿瘤、产科、骨科、重症医学、急诊、放射、超声、病理、检验、药事、护理、院内感染、门诊、医学研究伦理、病原微生物实验室生物安全、临床用血、健康体检、康复医学18个质控中心的建设工作。', '', '86-0571-56005600', NULL, NULL, 'https://www.hz-hospital.com/index/index/index_com.html', NULL, '10100000', '10330000', '10330100', '杭州市上城区浣纱路261号', 'No.261, Huansha Road, Hangzhou, P. R. Chi', NULL, 0, 0, '{\"ethicsData\":{\"cellStyleMap\":{},\"contacts\":[{\"cellStyleMap\":{},\"email\":\"1742208034@qq.com\",\"name\":\"陆蕴\"}],\"key1\":\"0571-56007507\"},\"geneticsOfficeData\":{\"cellStyleMap\":{},\"contacts\":[]},\"instructionData\":{\"cellStyleMap\":{},\"contacts\":[],\"key46\":\"浙江省杭州市浣纱路261号七号楼四楼\",\"key49\":\"0571-56007501；0571-56007405\",\"key50\":\"44688189@qq.com\"},\"pharmacyData\":{\"cellStyleMap\":{}},\"smoAndCroPlan\":{\"cellStyleMap\":{},\"smoAndCroList\":[]}}', NULL, NULL, 1, 1564022101162, 10010, 1583919484826, 0, '', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
