INSERT INTO `t_hospital` VALUES (100203, NULL, NULL, 1001, 1009, '河北医科大学第二医院', 'The second hospital of Hebei Medical University', 'http://www.hb2h.com/extranet/pages/one/info.htm?pid=4', '', ' 总机：0311-87046901 ', NULL, 'pub@hb2h.com', 'http://www.hb2h.com/', NULL, '10100000', '10130000', '10130100', '河北省石家庄市和平西路215号', NULL, NULL, 0, 0, '{\"ethicsData\":{\"cellStyleMap\":{},\"contacts\":[{\"cellStyleMap\":{},\"email\":\"HB2HLL@126.com\",\"name\":\"马景学\",\"title\":\"主任委员 \"},{\"cellStyleMap\":{},\"name\":\"康诺敏\",\"title\":\"副主任委员\"}],\"key1\":\"0311-66002811，0311-66002837\",\"key2\":\"0311-66002811\",\"key3\":\"HB2HLL@126.com\",\"key32\":\"伦理审查递交材料注意事项\\n1、请仔细阅读清单按要求准备。\\n2、临床试验立项申请表（1份）、伦理审查申请表（1份）；随纸质版资料上交至伦理办公室。\\n3、临床试验立项申请表发电子版至机构邮箱hb2yjg@163.com。\\n4、电子版上会资料、审查文件清单、汇报PPT（可自用模版，但必须包含PPT模版中的内容）发至伦理邮箱HB2HLL@126.COM。\\n5、研究团队成员分工表可参考机构模版“研究团队成员分工表”。\\n6、研究者履历需使用机构模版，可从官网下载；研究者GCP培训证复印件科室秘书处均有备案可直接复印。\\n7、伦理会前需将伦理费打款回执发送至伦理邮箱。\\n8、研究费打款回执发送至机构邮箱，并备注试验名称。\\n9、本机构建有CRA、CRC微信群，递交资料时需申请加入。\",\"key5\":\"河北省石家庄市新华区和平西路215号图书馆楼一楼\",\"key7\":\"http://www.hb2h.com/extranet/pages/one/trainingInfo.htm?tcid=2&pid=1582\"},\"geneticsOfficeData\":{\"cellStyleMap\":{},\"contacts\":[]},\"instructionData\":{\"cellStyleMap\":{},\"contacts\":[{\"cellStyleMap\":{},\"email\":\"shumei-wang@163.com；aaaa2089@126.com\",\"name\":\"王淑梅\",\"telePhone\":\"0311－7046901－2089\",\"title\":\"机构办主任\"},{\"cellStyleMap\":{},\"name\":\"郭力\",\"title\":\"机构主任\"},{\"cellStyleMap\":{},\"name\":\"张志清\",\"title\":\"机构副主任\"},{\"cellStyleMap\":{},\"email\":\"liuxiujv@sohu.com\",\"name\":\"刘秀菊\",\"telePhone\":\"0311-66002771\",\"title\":\"机构秘书\"},{\"cellStyleMap\":{},\"name\":\"赵树藩\",\"telePhone\":\"0311-66002089\",\"title\":\"负责人\"}],\"key1\":\"http://www.hb2h.com/extranet/pages/slogan/info.htm?iid=7677&ccid=21\",\"key10\":\"是\",\"key11\":\"是\",\"key13\":\"在确认***款打到医院账户后通知申办方组织召开专业启动会\",\"key26\":\"否\",\"key32\":\"机构办对每个试验至少进行两次质控：试验前期（随机3例）和试验结束\",\"key33\":\"药物临床试验需在试验完成（中心小结盖章前）且数据未锁库之前进行一次自查工作，CRA及CRC各自完成所负责工作的自查，完成“CRA自查内容表（试验结束）”以及“CRC自查内容表（试验结束）”，如机构质控后发现问题将不再对问题进行修正，并将问题通告PI；器械临床试验自查工作参照药物临床试验；\",\"key35\":\"是\",\"key37\":\"1. 申办者进行自查/稽查之前CRA需要先与机构办公室主任联系，同时取得机构办主任的同意。\\n2. 自查/稽查具体日期CRA需要先与机构办进行预约。\\n3. 申办者派遣人员自查/稽查当日需携带单位介绍信原件和本人身份证复印件，机构办将此文件归档于试验的研究者文件夹中。\\n4. 在资料室填写“试验资料借阅登记本”并签名后，机构资料管理员将已归档的全部试验资料交由申办者派遣人员进行自查/稽查。\\n5. 自查/稽查结束后需将自查/稽查结果向机构办进行反馈，同时将自查/稽查报告留机构备案。\\n6. 自查/稽查过程中申办者派遣人员不得擅自更改和完善试验资料。\\n\",\"key46\":\"河北省石家庄市新华区和平西路215号\",\"key47\":\"0311-66002089\",\"key49\":\"0311-66002778\",\"key50\":\"hb2yjg@163.com\",\"key6\":\"是\"},\"pharmacyData\":{\"cellStyleMap\":{}},\"smoAndCroPlan\":{\"cellStyleMap\":{},\"smoAndCroList\":[]}}', '12130000401700390U', '王晓路', 1, 1564022101494, 10010, 1583919134084, 0, '', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
