INSERT INTO `t_hospital` VALUES (100237, NULL, NULL, 1001, 1009, '湖北省中医院', 'HUBEI PROVINCIAL HOSPITAL OF TCM', '    湖北省中医院暨湖北省中医药研究院、湖北中医药大学附属医院、国家中医临床研究基地，是全国唯一一所“四位一体”发展的省级三级甲等中医院，是国家中医药管理局首批全国示范中医院、国家临床药物试验机构、湖北省中医药数据中心。医院始建于1868年（清同治七年），前身是英国传教士杨格非博士创办的仁济医院，距今已有150年的历史。经过几代省中医人的辛苦耕耘，目前医院已发展成集医疗、教学、科研、保健、新药研发于一体的大型综合性省级中医院。2017年，医院被评为全国文明单位。', '', '医院总值班：027-88929057 门 诊电话：027-88929419', NULL, NULL, 'http://hbhtcm.com/', NULL, '10100000', '10420000', '10420100', '湖北省武汉市武昌区花园山4号', '', '430061', 0, 0, '{\"ethicsData\":{\"cellStyleMap\":{},\"contacts\":[{\"cellStyleMap\":{},\"email\":\"465449749@qq.com ；chen1664@foxmail.com\",\"name\":\"陈学军\"},{\"cellStyleMap\":{},\"email\":\"zxx737@163.com\",\"name\":\"张馨\"},{\"cellStyleMap\":{},\"email\":\"4453577@qq.com；wjhbzyy@163.com\",\"name\":\"吴珺\"}],\"key1\":\"027-88920956；027-88929115\",\"key3\":\"ec@hbhtcm.com\",\"key8\":\"工作日\"},\"geneticsOfficeData\":{\"cellStyleMap\":{},\"contacts\":[]},\"instructionData\":{\"cellStyleMap\":{},\"contacts\":[{\"cellStyleMap\":{},\"email\":\"ljzwd@163.com\",\"name\":\"刘建忠\",\"position\":\"机构办主任\",\"telePhone\":\"027-88929224，027－87172190\"},{\"cellStyleMap\":{},\"email\":\"xinnuo3277@126.com\",\"name\":\"李羚青\",\"telePhone\":\"027-88927130，027-88920837\"}],\"key4\":\"工作日\",\"key46\":\"湖北省武汉市武昌区花园山4号 药物临床试验机构办公室\",\"key49\":\"027-88920837，027-88927130\",\"key50\":\"xinnuo3277@126.com\"},\"pharmacyData\":{\"cellStyleMap\":{}},\"smoAndCroPlan\":{\"cellStyleMap\":{},\"smoAndCroList\":[]}}', NULL, NULL, 1, 1564022102425, 10010, 1583919196994, 0, '', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `t_hospital` VALUES (100238, NULL, NULL, 1005, 1009, '湖北省肿瘤医院', NULL, ' 湖北省肿瘤医院（湖北省肿瘤研究所）、华中科技大学同济医学院附属湖北肿瘤医院创建于1973年，是湖北省卫生健康委直属的集预防、医疗、康复、科研、教学于一体的大型三级甲等肿瘤专科医院，是美国巴菲特癌症中心姊妹医院、湖北省癌症中心、湖北省肿瘤防办依托单位，牵头湖北省肿瘤医学质量控制中心工作。医院设有国家药物临床试验基地、博士后流动站、湖北省、武汉市医保定点医院、新型农村合作医疗定点医院、商业保险定点医院。', '', '027-87670138（白天）\n027-87670078（午间、夜间、节假日）', NULL, NULL, 'http://www.hbch.com.cn/', NULL, '10100000', '10420000', '10420100', '武汉市洪山区卓刀泉南路116号', '', '430079', 0, 0, '{\"ethicsData\":{\"cellStyleMap\":{},\"contacts\":[{\"cellStyleMap\":{},\"email\":\"hbchlunli@163.com\",\"telePhone\":\"13971544027\"}],\"key1\":\"027-87671663\",\"key10\":\"每月月底\",\"key17\":\"国内多中心5350元，国外多中心8560元（含税）\",\"key2\":\"027-87670468\",\"key3\":\"hbchlunli@163.com\",\"key5\":\"湖北省武汉市卓刀泉南路116号湖北省肿瘤医院行政楼308办公室\",\"key8\":\"工作日下午2：30-4：00\"},\"geneticsOfficeData\":{\"cellStyleMap\":{},\"contacts\":[]},\"instructionData\":{\"cellStyleMap\":{},\"contacts\":[{\"cellStyleMap\":{},\"mobilePhone\":\"13971544027\",\"name\":\"许芳\"}],\"key46\":\"湖北省武汉市洪山区卓刀泉南路116号\",\"key49\":\"13971544027\",\"key5\":\"肿瘤\"},\"pharmacyData\":{\"cellStyleMap\":{}},\"smoAndCroPlan\":{\"cellStyleMap\":{},\"smoAndCroList\":[]}}', NULL, NULL, 1, 1564022102453, 10010, 1583919194286, 0, '', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `t_hospital` VALUES (100239, NULL, NULL, 1001, 1009, '湖南省儿童医院', NULL, ' 湖南省儿童医院位于省会长沙市雨花区梓园路86号，于1987年6月1日正式开院， 1995年通过卫生部三级甲等综合型儿童专科医院评审，2004年通过ISO9001/14001国际质量环境体系认证。2013年至2015年全面贯行美国JCI国际管理标准。2016年获批爱婴医院、国家儿童早期发展示范基地和药物临床试验机构，2017年成为中国出生缺陷干预救助基金会湖南省项目管理机构。', '', '0731-85600908', NULL, 'Hnsetyylzjd@163.com', 'http://www.et-hospital.com', NULL, '10100000', '10430000', '10430100', '湖南省长沙市梓园路86号', NULL, '410007', 0, 0, '{\"ethicsData\":{\"cellStyleMap\":{},\"contacts\":[{\"cellStyleMap\":{},\"name\":\"肖政辉\",\"title\":\"主任委员\"},{\"cellStyleMap\":{},\"mobilePhone\":\"15802516698\",\"name\":\"秦红文\",\"title\":\"伦理会秘书\"}],\"key1\":\"0731-85356014\",\"key10\":\"每月一次\",\"key17\":\"上会5000元\",\"key2\":\"0731-85356014\",\"key21\":\"快审3000元\",\"key3\":\"qhw100200@163.com\",\"key5\":\"湖南省长沙市雨花区梓园路86号 湖南省儿童医院临床试验伦理委员会办公室\",\"key7\":\"http://www.hnetyy.net/keyan/lingchuang/\",\"key8\":\"周一至周五 上午8:00~12:00 下午2:30~5:30\",\"key9\":\"每月下旬\"},\"geneticsOfficeData\":{\"cellStyleMap\":{},\"contacts\":[]},\"instructionData\":{\"cellStyleMap\":{},\"contacts\":[{\"cellStyleMap\":{},\"name\":\"谭李红\",\"title\":\"机构主任\"},{\"cellStyleMap\":{},\"name\":\"江训良\",\"telePhone\":\"0731-85356014\",\"title\":\"机构办主任\"},{\"cellStyleMap\":{},\"name\":\"肖政辉\",\"title\":\"机构办副主任\"},{\"cellStyleMap\":{},\"name\":\"万星驿\",\"telePhone\":\"0731-85356023\",\"title\":\"机构办秘书\"},{\"cellStyleMap\":{},\"title\":\"质控员\"}],\"key4\":\"周一至周五：上午8:00-12:00；下午2:30-5:30\",\"key46\":\"湖南省长沙市雨花区梓园路86号湖南省儿童医院药物临床试验机构办公室\",\"key47\":\"0731-85356094\",\"key49\":\"0731-85356023\",\"key50\":\"hnetgcp2013@163.com\"},\"pharmacyData\":{\"cellStyleMap\":{}},\"smoAndCroPlan\":{\"cellStyleMap\":{},\"smoAndCroList\":[]}}', NULL, NULL, 1, 1564022102480, 10010, 1583919231948, 0, '', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `t_hospital` VALUES (100240, NULL, NULL, 1005, 1009, '湖南省妇幼保健院', NULL, '湖南省妇幼保健院座落于长沙市芙蓉路与湘春路交汇处，创建于1947年，至今已有50余年的历史，是我国建院时间最早的省级妇幼保健机构之一。据统计，建院至今，在湖南省妇幼保健院诞生的新生命已逾10万，长沙市内有不少三代在此出生的家庭。50多年来，医院从建院时只有40张床位的专科诊所，发展成为拥有300张编制床位，担负着全省妇女儿童的保健、医疗、健康教育、科研教学、基层技术指导、妇幼卫生人员培训、妇幼卫生信息统计等多项任务的专业医疗保健机构，为国家级“三甲妇幼保健院”、“爱婴医院”、全国妇幼卫生人员培训基地、同济医科大学教学医院；是全省的“产前诊断中心”、“妇幼保健技术指导中心”、“生殖保健中心”、“乳腺疾病防治中心”、“新生儿疾病筛查中心”、“母乳喂养指导中心”和“新生儿抚触技术培训中心”；并荣获省级“文明医院”、“四满意”服务竞赛优胜单位等荣誉称号。年均诊治门诊病人30万余人次，收治住院病人1.5万余人次。', '', '0731-4332201,4332238', NULL, NULL, 'http://www.hunanfy.com/', NULL, '10100000', '10430000', '10430100', '湖南省长沙市湘春路23号', NULL, '410008', 0, 0, '{\"ethicsData\":{\"cellStyleMap\":{},\"contacts\":[{\"cellStyleMap\":{},\"name\":\"梁松岳\"},{\"cellStyleMap\":{},\"name\":\"佘志华\"}],\"key1\":\"13874800551，0731-84332386\",\"key10\":\"每年4次以上\",\"key3\":\"shegua@163.com，744740861@qq.com\",\"key5\":\"湖南省长沙市开福区湘春路53号湖南省妇幼保健院创新楼23楼\"},\"geneticsOfficeData\":{\"cellStyleMap\":{},\"contacts\":[]},\"instructionData\":{\"cellStyleMap\":{},\"contacts\":[{\"cellStyleMap\":{},\"name\":\"周绍明\",\"title\":\"机构负责人\"},{\"cellStyleMap\":{},\"name\":\"吴雅莉\",\"title\":\"办公室主任\"},{\"cellStyleMap\":{},\"name\":\"文晓柯\",\"title\":\"办公室副主任\"},{\"cellStyleMap\":{},\"name\":\"\\t王红丽\",\"title\":\"秘书\"},{\"cellStyleMap\":{},\"name\":\"郑艺\",\"title\":\"质控员\"},{\"cellStyleMap\":{},\"name\":\"赵璐茜\",\"title\":\"资料管理员\"},{\"cellStyleMap\":{},\"name\":\"凌超\",\"title\":\"药物管理员\\t\"},{\"cellStyleMap\":{},\"name\":\"梁娟\"}],\"key4\":\"周一至周五\",\"key46\":\"湖南省妇幼保健院创新楼2303室\",\"key49\":\"0731-84332117\",\"key50\":\"yali161227@163.com\"},\"pharmacyData\":{\"cellStyleMap\":{}},\"smoAndCroPlan\":{\"cellStyleMap\":{},\"smoAndCroList\":[]}}', NULL, NULL, 1, 1564022102509, 10010, 1583919223515, 0, '', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
