INSERT INTO `t_hospital` VALUES (100243, NULL, NULL, 1001, 1009, '湖南省人民医院', NULL, '湖南省人民医院（湖南师范大学附属第一医院）是湖南省政府直属唯一集医教研于一体的三级甲等综合医院，是湖南师范大学临床医学院。设有湖南省老年医学研究所、湖南省急救医学研究所和湖南师范大学临床转化医学研究所、湖南师范大学医院精细化研究所。是国家博士后科研工作站和国家药物临床试验机构，创办了实用休克杂志，是重症代谢组学湖南省重点实验室挂靠单位。是中华医学会科普分会副主任委员单位、中国医师协会儿科医师分会副会长单位，是湖南省康复医学会、湖南省医疗整形美容协会挂靠单位，是湖南省医学会肝胆专业委员会、急诊专业委员会、介入医学委员会、消化专业委员会、全科医学专业委员会、科普专业委员会主委单位。 ', '', '0731-82278071，82278048 ，82278011', NULL, NULL, 'http://www.hnsrmyy.com/', NULL, '10100000', '10430000', '10430100', '湖南省长沙市解放西路61号', '', NULL, 0, 0, '{\"ethicsData\":{\"cellStyleMap\":{},\"contacts\":[{\"cellStyleMap\":{},\"name\":\"刘景诗\",\"title\":\"主任委员\"},{\"cellStyleMap\":{},\"name\":\"李璟\",\"title\":\"伦理办秘书\"},{\"cellStyleMap\":{},\"name\":\"谭波宇\",\"title\":\"伦理办秘书\"},{\"cellStyleMap\":{},\"name\":\"曾丹\",\"title\":\"伦理办秘书\"}],\"key1\":\"0731-83929085\",\"key10\":\"1次/月\",\"key17\":\"3000-6000元 根据研究时长及入组数量确定\",\"key3\":\"lunlikyb@163.com\",\"key5\":\"湖南省长沙市解放西路61号 医院综合楼910\",\"key8\":\"QQ预约\",\"key9\":\"每月第三或四周\"},\"geneticsOfficeData\":{\"cellStyleMap\":{},\"contacts\":[]},\"instructionData\":{\"cellStyleMap\":{},\"contacts\":[],\"key4\":\"周一~周五（8:00-12:00 /14:30-17:30）\",\"key46\":\"湖南省长沙市解放西路61号 医院综合楼504\",\"key49\":\"0731-83929310\",\"key50\":\"kybgcp@163.com\"},\"pharmacyData\":{\"cellStyleMap\":{}},\"smoAndCroPlan\":{\"cellStyleMap\":{},\"smoAndCroList\":[]}}', NULL, NULL, 1, 1564022102588, 10010, 1583919228660, 0, '', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
