INSERT INTO `t_hospital` VALUES (100247, NULL, NULL, 1002, 1009, '湖南中医药大学第一附属医院', 'The First Hospital of Hun University Of Chinese Medicine', '湖南中医药大学第一附属医院创建于1963年，是“七五”期间全国七所重点项目建设的中医院之一，2008年入选国家中医临床研究基地，2017年入选“中医药传承创新工程”项目建设单位。经过历代中医附一人的不懈努力，医院事业不断发展，现已成为一所中医特色突出、综合功能完善、名老中医荟萃，重点专科云集、研究创新能力强、管理规范的现代化、综合性、研究型中医医院，成为湖南省首家三级甲等中医医院、湖南省中医及中西医结合医、教、研中心和龙头，医院的中医特色和综合能力建设已跻身全国中医医院先进水平。医院占地面积110亩，现有建筑面积24万平方米，编制床位1800张，年门诊人次160余万人次，年住院人次近7万。', '', '0731-85600120', '0731-85600709', 'syfzb9068@sina.cn', 'http://www.hnzyfy.com/', NULL, '10100000', '10430000', '10430100', '湖南长沙韶山中路95号', '', NULL, 0, 0, '{\"ethicsData\":{\"cellStyleMap\":{},\"contacts\":[{\"cellStyleMap\":{},\"name\":\"刘平安\",\"title\":\"主任委员\"},{\"cellStyleMap\":{},\"name\":\"王华\",\"telePhone\":\"0731-85369233\",\"title\":\"伦理办公室主任\"},{\"cellStyleMap\":{},\"email\":\"742604945@qq.com\",\"name\":\"赵鸿\",\"title\":\"伦理办公室秘书\"}],\"key1\":\"0731-85369233\",\"key10\":\"每月一次\",\"key17\":\"5000元（初审）\",\"key2\":\"0731-85369233\",\"key3\":\"65138681@qq.com\",\"key5\":\"湖南省长沙市韶山中路95号行政楼325室\",\"key8\":\"工作日（8:00-12:00/14:30-17:30）\",\"key9\":\"视项目情况确定\"},\"geneticsOfficeData\":{\"cellStyleMap\":{},\"contacts\":[]},\"instructionData\":{\"cellStyleMap\":{},\"contacts\":[],\"key4\":\"工作日（8:00-12:00/14:30-17:30）\",\"key46\":\"湖南省长沙市韶山中路95号行政楼323室\",\"key47\":\"0731-85600717\",\"key50\":\"898786532@qq.com\"},\"pharmacyData\":{\"cellStyleMap\":{}},\"smoAndCroPlan\":{\"cellStyleMap\":{},\"smoAndCroList\":[]}}', NULL, NULL, 1, 1564022102697, 10010, 1583919226089, 0, '', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `t_hospital` VALUES (100248, NULL, NULL, 1001, 1009, '湖州市中心医院', 'HUZHOU CENTER HOSPITAL', '湖州市中心医院创建于1944年，是集医疗、科研、教学、预防、保健、康复于一体的三级甲等综合性医院。2014年挂牌浙江大学湖州医院。医院建筑面积约15万平方米，核定床位1200张，开放床位1500张。年门急诊量145万/人次，出院病人6.24万/人次，担负着湖州及周边地区300多万人口的医疗、保健工作。是湖州师范学院浙北临床医学院，承担着浙江大学医学院、苏州大学医学院、温州医学院等20多所院校的教学任务。', '', '0572-2023301 ，2023219 ，2055120', NULL, NULL, 'http://www.hzhospital.com/#', NULL, '10100000', '10330000', '10330800', '浙江省湖州市红旗路198号。', '', NULL, 0, 0, '{\"ethicsData\":{\"cellStyleMap\":{},\"contacts\":[{\"cellStyleMap\":{},\"mobilePhone\":\"15906729121\",\"name\":\"蒋凤琴\",\"position\":\"伦理秘书\"}],\"key1\":\"0572-2709719\",\"key2\":\"0572-2709719\",\"key3\":\"hzszxyyll@163.com\",\"key5\":\"湖州市中心医院11号楼3楼伦理办公室\",\"key8\":\"周二至周四 上午08:00-17:00，下午13:30-17:00\"},\"geneticsOfficeData\":{\"cellStyleMap\":{},\"contacts\":[]},\"instructionData\":{\"cellStyleMap\":{},\"contacts\":[{\"cellStyleMap\":{},\"email\":\"15715896580@126.com\",\"mobilePhone\":\"15715896580\",\"name\":\"沈敏敏\",\"position\":\"机构办副主任/项目接洽人\"},{\"cellStyleMap\":{},\"email\":\"2293570019@qq.com\",\"mobilePhone\":\"18258050056\",\"name\":\"王沛\",\"position\":\"机构办秘书/遗传办联系人\"}],\"key4\":\"周二至周五 上午8:00-12:00 下午13:30-17:00\\n\",\"key46\":\"浙江省湖州市红旗路198号3号楼5楼药物临床试验机构办\\n\",\"key47\":\"0572-2709718\",\"key49\":\"0572-2709718\",\"key50\":\"hzpharma@126.com\"},\"pharmacyData\":{\"cellStyleMap\":{}},\"smoAndCroPlan\":{\"cellStyleMap\":{},\"smoAndCroList\":[]}}', NULL, NULL, 1, 1564022102724, 10010, 1583919479916, 0, '', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
