INSERT INTO `t_hospital` VALUES (100261, NULL, NULL, 1001, 1009, '黄冈市中心医院', 'Huanggang CentralHospital', '黄冈市中心医院位于古城黄州，始建于1890年，是黄冈市唯一集医疗、教学、科研、预防、急救、康复为一体的国家综合性三级甲等医院（1996年）、湖北省三级优秀医院（2009年）、国际紧急救援中心网络医院、爱婴医院、长江大学附属医院、湖北科技学院教学医院，获批国家住院医师规范化培训基地、国家全科医生临床培养基地、国家药物临床试验机构、湖北省专科护士培训基地。医院现编制床位1050张，开设有一级诊疗科目24个、二级诊疗科目50个，职能科室25个；创建5个省级临床重点（建设）专科，21个市级甲类临床重点专科。万元以上大、中型医疗设备1041台（套），拥有3.0T核磁共振、128排iCT、64排螺旋CT、DSA、ECT、LA等高端影像设备，设备净值2.01亿元，总资产11.39亿元。', '', '0713-8625114', NULL, NULL, 'http://www.hgyy.org.cn/', NULL, '10100000', '10420000', '10421100', '黄冈市黄州区考棚街11号', '', NULL, 0, 0, '{\"ethicsData\":{\"cellStyleMap\":{},\"contacts\":[],\"key1\":\"0713-8625980\",\"key17\":\"2000-5000元\",\"key19\":\"是\",\"key20\":\"是\",\"key3\":\"hgsgcp@163.com\",\"key9\":\"一般为每月中旬\"},\"geneticsOfficeData\":{\"cellStyleMap\":{},\"contacts\":[]},\"instructionData\":{\"cellStyleMap\":{},\"contacts\":[{\"cellStyleMap\":{},\"mobilePhone\":\"13986568491\",\"name\":\"张美祥\"}],\"key1\":\"http://www.hgyy.org.cn/Category_133/Index.aspx\",\"key12\":\"2000-5000元\",\"key46\":\"湖北省黄冈市黄州区考棚街11号\",\"key49\":\"13986568491\",\"key9\":\"三级甲等\"},\"pharmacyData\":{\"cellStyleMap\":{}},\"smoAndCroPlan\":{\"cellStyleMap\":{},\"smoAndCroList\":[]}}', NULL, NULL, 1, 1564022103079, 10010, 1583919193485, 0, '', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
