INSERT INTO `t_hospital` VALUES (100281, NULL, NULL, 1001, 1010, '嘉兴市第一医院', 'The First Hospital of Jiaxing', '嘉兴市第一医院（嘉兴学院附属医院）位于中国革命红船的启航地——嘉兴南湖之畔。医院始建于1920年，经过几代人的艰苦创业，已成为嘉兴市规模最大、设施齐全、技术力量雄厚的集医疗、科研、教学、预防、康复于一体的三级甲等综合医院。2019年8月21日，挂牌成为嘉兴学院直属附属医院。先后荣获全国“优化优质护理”示范医院、全国改善医疗服务创新医院、全国模范职工之家、第二批全国社会工作服务示范单位、省级文明单位、省健康促进医院、省援外医疗工作先进集体、省干部保健工作先进集体等荣誉称号。', '', '总机电话：0573-82519999 医院总机：0573-82082937 预约咨询：0573-82153018 急诊电话：0573-82095099', NULL, NULL, 'http://www.jxdyyy.com/', NULL, '10100000', '10330000', '10330400', '浙江省嘉兴市中环南路1882号', '', NULL, 0, 0, '{\"ethicsData\":{\"cellStyleMap\":{},\"contacts\":[],\"key1\":\"0573-89976378\",\"key2\":\"0573-89976378\",\"key3\":\"jxdyyyll@163.com\",\"key5\":\"浙江省嘉兴市中环南路1882号嘉兴市第一医院门诊5楼C区\"},\"geneticsOfficeData\":{\"cellStyleMap\":{},\"contacts\":[]},\"instructionData\":{\"cellStyleMap\":{},\"contacts\":[],\"key4\":\"工作时间：8:00-11:30 14:00-17:00\",\"key46\":\"浙江省嘉兴市中环南路1882号嘉兴市第一医院门诊5楼C区药物临床试验机构办公室\",\"key47\":\"0573-89976377\",\"key49\":\"0573-89976377\\n，0573-82519531\",\"key50\":\"jxdyyygcp@163.com\"},\"pharmacyData\":{\"cellStyleMap\":{}},\"smoAndCroPlan\":{\"cellStyleMap\":{},\"smoAndCroList\":[]}}', NULL, NULL, 1, 1564022103636, 10010, 1583919478441, 0, '', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
