INSERT INTO `t_hospital` VALUES (100289, NULL, NULL, 1005, 1009, '江西省精神病院', 'Jiangxi mental hospital', '江西省精神病院（江西省精神卫生中心）系隶属于南昌市卫生健康委员会的全民所有制差额拨款事业单位，座落于南昌市城东、玉带河畔、青山湖之滨，总占地面积约67亩、总建筑面积62829.57m2。现有编制床位660张，设有精神科门诊、心理门诊、专家门诊以及情感障碍、儿童精神障碍、睡眠障碍等专病门诊；住院部设有10个病区（包括精神科、老年科、心理科、儿少精神科），另设有公共卫生科和精神病学司法鉴定科。', '', '18179133112//0791- 88173733', NULL, NULL, 'http://www.jxsjsby.cn/', NULL, '10100000', '10360000', '10360100', ' 南昌市东湖区上坊路43号', '', NULL, 0, 0, '{\"ethicsData\":{\"cellStyleMap\":{},\"contacts\":[]},\"geneticsOfficeData\":{\"cellStyleMap\":{},\"contacts\":[]},\"instructionData\":{\"cellStyleMap\":{},\"contacts\":[{\"cellStyleMap\":{},\"mobilePhone\":\"18970800899\",\"name\":\"廖老师\"}],\"key49\":\"18970800899\",\"key50\":\"JXMHGCP@163.com\"},\"pharmacyData\":{\"cellStyleMap\":{}},\"smoAndCroPlan\":{\"cellStyleMap\":{},\"smoAndCroList\":[]}}', NULL, NULL, 1, 1564022103849, 10010, 1583919286246, 0, '', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
