INSERT INTO `t_hospital` VALUES (100310, NULL, NULL, 1001, 1009, '金华市中心医院', 'jinhuamunicipal central hospital', '金华市中心医院是由建于1910年的金华福音医院和1946年的省立金华医院合并发展而来，至今已有109年历史，是浙江中西部地区集医疗、科研、教学、预防、保健、康复为一体的综合性三级甲等医院。2012年挂牌浙江大学金华医院。2015年与台北医学大学附设医院结为友好医院。', '', '0579-82338512', '321000', NULL, 'http://www.jhzxyy.cn/', NULL, '10100000', '10330000', '10330700', '浙江省金华市人民东路365号', '', NULL, 0, 0, '{\"ethicsData\":{\"cellStyleMap\":{},\"contacts\":[],\"key1\":\"0579-82556396\",\"key2\":\"0579-82556396\",\"key3\":\"jhszxyyllwyh@163.com\",\"key5\":\"浙江省金华市人民东路365号行政楼J06室\"},\"geneticsOfficeData\":{\"cellStyleMap\":{},\"contacts\":[]},\"instructionData\":{\"cellStyleMap\":{},\"contacts\":[{\"cellStyleMap\":{},\"email\":\"jhszxyygcp@163.com\",\"name\":\"张伟东\",\"telePhone\":\"0579-82552563\"}],\"key4\":\"工作日： 8:00-11:30 ；13：30-17:00\",\"key46\":\"浙江省金华市人民东路365号行政楼J03室\",\"key47\":\"0579-82552563\",\"key49\":\"0579-82552563\",\"key50\":\"0579-82552563\"},\"pharmacyData\":{\"cellStyleMap\":{}},\"smoAndCroPlan\":{\"cellStyleMap\":{},\"smoAndCroList\":[]}}', NULL, NULL, 1, 1564022104423, 10010, 1583919472585, 0, '', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
