INSERT INTO `t_hospital` VALUES (100317, NULL, NULL, 1001, 1009, '荆州市中心医院', 'JINGZHOU CENTRAL HOSPITAL', '荆州市中心医院始建于1950年3月，坐落于国家历史文化名城湖北荆州古城中心，相传是名医华佗为武圣关羽刮骨疗毒遗址所在地，历史悠久，底蕴深厚。是江汉平原集医疗、教学、科研、预防、康复为一体的大型综合性三级优秀甲等医院，是湖北省地市级区域医疗中心、华中科技大学同济医学院附属医院、长江大学第二临床医学院，是国家级胸痛中心、国家级住院医师规范化培训基地、中国医院百强院、湖北省卒中急救地图推荐医院、长江大学硕士学位点，是全国优秀医院院长单位、湖北省医院协会副会长单位。', '', '0716-8881888', NULL, NULL, 'http://www.jzzxyy.com.cn/huanzhe/', 'jzszxyy', '10100000', '10420000', '10421000', '湖北省荆州市荆州区荆中路60号（荆州古城花台）', '', NULL, 0, 0, '{\"ethicsData\":{\"cellStyleMap\":{},\"contacts\":[{\"cellStyleMap\":{},\"name\":\"李华荣\",\"telePhone\":\"0716-8499992\",\"title\":\"伦理委员会秘书\"}],\"key1\":\"0716-8497426\",\"key10\":\"没有固定频率，基本上一个月一次\",\"key17\":\"3400\",\"key2\":\"0716-8498268\",\"key21\":\"1700\",\"key3\":\"lihuarong@163.com\",\"key5\":\"荆州市荆州区人民路1号学生公寓1号楼2楼\",\"key8\":\"周一至周五\"},\"geneticsOfficeData\":{\"cellStyleMap\":{},\"contacts\":[]},\"instructionData\":{\"cellStyleMap\":{},\"contacts\":[],\"key1\":\"http://www.jzzxyy.com.cn/column/yiyuangaikuang/zuzhijigou/zhinengkeshi/yaowulinchuangshiyanjidi/\",\"key4\":\"周一至周四工作时间\",\"key46\":\"湖北省荆州市荆中路60号\",\"key47\":\"0716-8497225\",\"key49\":\"0716-8497225\",\"key50\":\"hbjzyyywlc@163.com\",\"key9\":\"三级甲等\"},\"pharmacyData\":{\"cellStyleMap\":{}},\"smoAndCroPlan\":{\"cellStyleMap\":{},\"smoAndCroList\":[]}}', NULL, NULL, 1, 1564022104612, 10010, 1583919193361, 0, '', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `t_hospital` VALUES (100318, NULL, NULL, 1001, 1009, '井冈山大学附属医院', NULL, '井冈山大学附属医院始建于1973年，位于革命摇篮井冈山所在地——江西省吉安市中心城区，是一所集医疗、教学、科研、预防保健、康复、急救为一体的省直综合性三级甲等医院。医院占地120余亩，建筑面积3.2万平方米，净资产6.05亿元，编制床位数1510张，实际开放床位数595张，2019年度出院病人21242人次，总诊疗367518人次。建设中的新综合大楼（全科医生培训基地）规划建设用地30476平方米，建筑面积90227平方米，规划床位626张，目标建成集医疗、教学、科研和培训为一体的学科全面、环境一流的现代化综合大楼。', '', '0796-8237091', NULL, NULL, 'http://fsyy.jgsu.edu.cn/', NULL, '10100000', '10360000', '10360800', '江西省吉安市吉州区井冈山大道110号', '', '343000', 0, 0, '{\"ethicsData\":{\"cellStyleMap\":{},\"contacts\":[]},\"geneticsOfficeData\":{\"cellStyleMap\":{},\"contacts\":[]},\"instructionData\":{\"cellStyleMap\":{},\"contacts\":[],\"key46\":\"江西省吉安市吉州区井冈山大道110号\",\"key49\":\"0796-8237772\",\"key50\":\"ywk8221065@163.com\"},\"pharmacyData\":{\"cellStyleMap\":{}},\"smoAndCroPlan\":{\"cellStyleMap\":{},\"smoAndCroList\":[]}}', NULL, NULL, 1, 1564022104640, 10010, 1583919283250, 0, '', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
