INSERT INTO `t_hospital` VALUES (100320, NULL, NULL, 1001, 1009, '九江市第一人民医院', 'Jiu Jiang NO.1Peoples Hospital', ' 九江市第一人民医院创建于1893年，江西南昌大学附属九江医院、四川大学附属华西医院、首都医科大学附属北京友谊医院协作医院。孙中山先生曾为医院题写“生命活水”院名，如今医院已发展成为一所集医疗、科研、教学、预防、保健、康复、急救为一体的九江市规模较大的三级甲等综合医院。承担着全市500万人口的医疗、保健和部分社区卫生服务等任务。 ', '', '0792-8582052', NULL, NULL, 'http://www.jxjjsdyrmyy.cn/', NULL, '10100000', '10360000', '10360400', '九江八里湖新区体育馆往前800米', '', NULL, 0, 0, '{\"ethicsData\":{\"cellStyleMap\":{},\"contacts\":[]},\"geneticsOfficeData\":{\"cellStyleMap\":{},\"contacts\":[]},\"instructionData\":{\"cellStyleMap\":{},\"contacts\":[{\"cellStyleMap\":{},\"email\":\"huxinglan321@163.com\",\"mobilePhone\":\"18679219933\",\"name\":\"胡主任\"}],\"key49\":\"0792-8178820；18679219933\"},\"pharmacyData\":{\"cellStyleMap\":{}},\"smoAndCroPlan\":{\"cellStyleMap\":{},\"smoAndCroList\":[]}}', NULL, NULL, 1, 1564022104695, 10010, 1583919284693, 0, '', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `t_hospital` VALUES (100321, NULL, NULL, 1001, 1009, '九江学院附属医院', 'Jiujiang University Hospital', ' \n\n    九江学院临床医学院/附属医院始建于1877年，前身是法籍天主教神父董若望创建的“圣味增爵医院”，为江西省第一所西医院，西医由此传入九江。现已发展成为拥有完善的基础设施和雄厚的医疗技术服务水平、学科设置齐全、师资力量雄厚、人才资源丰富的江西省三级甲等综合医院。', '', '2180100', NULL, NULL, '九江市浔阳东路57号', NULL, '10100000', '10360000', '10360400', '九江市浔阳东路57号', '', NULL, 0, 0, '{\"ethicsData\":{\"cellStyleMap\":{},\"contacts\":[],\"key1\":\"0792-2180161\",\"key5\":\"九江市浔阳东路57号\"},\"geneticsOfficeData\":{\"cellStyleMap\":{},\"contacts\":[]},\"instructionData\":{\"cellStyleMap\":{},\"contacts\":[{\"cellStyleMap\":{},\"mobilePhone\":\"13870263921\",\"name\":\"李杰\",\"position\":\"机构秘书\"}],\"key49\":\"0792-2180161\",\"key50\":\"jjxyfsyykjk@163.com\"},\"pharmacyData\":{\"cellStyleMap\":{}},\"smoAndCroPlan\":{\"cellStyleMap\":{},\"smoAndCroList\":[]}}', NULL, NULL, 1, 1564022104723, 10010, 1583919285569, 0, '', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
