INSERT INTO `t_hospital` VALUES (100333, NULL, NULL, 1001, 1009, '昆明市第一人民医院', 'The First Hospital Of Kunming', '我院始建于1914年，前身是法国人创建的“甘美医院”，后与朱德任云南警察厅长时开设的警察医院合并，1963年正式改名为“昆明市第一人民医院”。历经近百年的建设发展，现已成为一所集医疗、教学、科研、预防、保健为一体的大型综合性三级甲等医院。我院同时也是昆明学院第一附属医院。\n\n医院共有2个院区，院本部占地面积38亩（青年路504号）、甘美医院占地面积120亩（北京路1228号），总业务用房22万余平方米。设有临床业务科室35个、医技科室16个和1个云南省最大的社区医院——昆明市第一人民医院星耀医院。\n\n编制床位2100张，职工3300余人。其中：高级职称291人，博士25人、硕士286人，博士和硕士生导师30人，享受国务院特殊津贴6人，省政府特殊津贴专家5人，省市级有突出贡献优秀专业技术人员18人，省市级中青年学术和技术带头人20人，云岭学者1人，云岭名医2人，春城人才奖2人，国家干细胞临床研究机构1个，省级博士后工作站1个，院士工作站1个，是国家住院医师规范化培训基地（20个专业）、国家临床药师规范化培训基地（4个专业）。', '', '0871-63188200', NULL, NULL, 'http://www.kmsdyrmyy.com/index.html', 'gh_76d130850d5a', '10100000', '10530000', '10530100', '云南省昆明市西山区青年路504号', '', '650034', 0, 0, '{\"ethicsData\":{\"cellStyleMap\":{},\"contacts\":[{\"cellStyleMap\":{},\"name\":\"李兰\",\"title\":\"医学院主任\"},{\"cellStyleMap\":{},\"name\":\"陈桂淑\",\"title\":\"医学院秘书\"}],\"key1\":\"0871-67390682\",\"key2\":\"0871-63188091\",\"key3\":\"kmsyiyll@163.com\"},\"geneticsOfficeData\":{\"cellStyleMap\":{},\"contacts\":[]},\"instructionData\":{\"cellStyleMap\":{},\"contacts\":[{\"cellStyleMap\":{},\"name\":\"曹海鹰\",\"title\":\"机构主任\"},{\"cellStyleMap\":{},\"name\":\"宋沧桑\",\"title\":\"机构办主任\"},{\"cellStyleMap\":{},\"name\":\"李兴德\",\"title\":\"机构秘书\"},{\"cellStyleMap\":{},\"name\":\"陆维\",\"title\":\"机构秘书\"},{\"cellStyleMap\":{},\"name\":\"张阳\",\"title\":\"质控员\"},{\"cellStyleMap\":{},\"name\":\"秦芳\",\"title\":\"药物管理员\"}],\"key46\":\"云南省昆明市青年路504号昆明市第一人民医院门诊5楼506室\",\"key48\":\"650034\",\"key49\":\"0871-64162339,0871-67390680\"},\"pharmacyData\":{\"cellStyleMap\":{}},\"smoAndCroPlan\":{\"cellStyleMap\":{},\"smoAndCroList\":[]}}', '1253010043136149XW\n', '李立 ', 1, 1564022105064, 10010, 1583919463340, 0, '', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
