INSERT INTO `t_hospital` VALUES (100335, NULL, NULL, 1001, 1009, '昆明市延安医院', NULL, '昆明市延安医院前身为上海市延安医院，1970年从上海整体内迁，经过近50年的建设，目前已发展成为学科门类齐全，技术力量雄厚，诊疗设备先进，科研实力强大的综合性临床及科研教学医院。\n\n医院于1993年被卫生部评为国家三级甲等医院，是全国百姓放心示范医院、全国文明单位、国家级心脏移植中心、国家级冠心病介入/结构性心脏病介入/心律失常介入专业培训基地、国家级博士后科研工作站设站单位、国家级干细胞临床研究备案机构、国家级药物临床试验机构、国家级医疗器械临床试验备案机构、国家级临床药师培训基地、国家级住院医师规范化培训基地、国家级全科医师培训基地、云南省住院医师规范化培训基地、昆明市基层医师培训基地。\n\n医院地处昆明市中心，占地78亩，核定床位1498张，40个临床和医技科室。现有2个国家级、20个省级和11个市级临床重点专科，5个省级和2个市级研究中心/重点实验室，8个省级和市级创新团队，37个省级和市级内设研究机构/技术中心,10个二级岗位人才，10个云南省万人计划名医专项人才，120个博导和硕导,23个云南省高层次卫生技术人才，49个省级和市级中青年学术和技术带头人和后备人选，161个昆明市“十百千”工程人才。', '', '86-0871-6311073', '0871-63627731', 'kmyayy@163.com', 'http://kmyayy.com/', 'yanan_hospital', '10100000', '10530000', '10530100', '云南省昆明市人民东路245号', '', '650051', 0, 0, '{\"ethicsData\":{\"cellStyleMap\":{},\"contacts\":[{\"cellStyleMap\":{},\"name\":\"谷欣\",\"title\":\"主任委员\"},{\"cellStyleMap\":{},\"name\":\"李汝红\",\"title\":\"副主任委员\"},{\"cellStyleMap\":{},\"name\":\"王畅\",\"title\":\"秘书\"}],\"key1\":\"0871-63211018\",\"key3\":\"yayyiec@163.com\"},\"geneticsOfficeData\":{\"cellStyleMap\":{},\"contacts\":[]},\"instructionData\":{\"cellStyleMap\":{},\"contacts\":[],\"key46\":\"云南省昆明市人民东路245号\",\"key48\":\"650051\",\"key49\":\"0871-63211318\",\"key50\":\"yayyjgb@163.com\"},\"pharmacyData\":{\"cellStyleMap\":{}},\"smoAndCroPlan\":{\"cellStyleMap\":{},\"smoAndCroList\":[]}}', NULL, NULL, 1, 1564022105117, 10010, 1583919463364, 0, '', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
