INSERT INTO `t_hospital` VALUES (100343, NULL, NULL, 1001, 1009, '丽水市人民医院', NULL, '丽水市人民医院是集医疗、科研、教学、康复、健康管理、司法鉴定为一体的三级甲等综合医院。医院历史悠久，实力雄厚，享誉浙西南地区。医院坚持以“仁爱、求精、奋进、协作、创新、务实”的丽医精神，追崇“打造技术精心、质量称心、服务贴心、环境舒心、安全放心的浙西南区域最具影响力的现代化医院”的愿景。围绕“服务病人、服务临床、服务职工”的工作理念，积极创建人本和谐医院。', '', '0578-2780222', NULL, NULL, 'http://www.ls-hospital.com/', NULL, '10100000', '10330000', '10331100', '浙江省丽水市莲都区大众街15号', '', NULL, 0, 0, '{\"ethicsData\":{\"cellStyleMap\":{},\"contacts\":[{\"cellStyleMap\":{},\"name\":\"施志超\",\"position\":\"伦理秘书\",\"telePhone\":\"0578-2780158\"}],\"key1\":\"0578-2780158\",\"key2\":\"0578-2780278\",\"key5\":\"丽水市莲都区大众街15号丽水市人民医院1号楼6层\",\"key8\":\"上午8:00-11:30,下午14:30-17:30 (下午时间根据时令会有所调整)\"},\"geneticsOfficeData\":{\"cellStyleMap\":{},\"contacts\":[]},\"instructionData\":{\"cellStyleMap\":{},\"contacts\":[{\"cellStyleMap\":{},\"email\":\"lslcsy@126.com\",\"mobilePhone\":\"0578-2780158；18957097928\",\"name\":\"王华富\",\"position\":\"机构秘书\"}],\"key4\":\"上午8:00-11:30,下午14:30-17:30 (下午时间根据时令会有所调整)\",\"key46\":\"浙江省丽水市莲都区大众街15号丽水市人民医院1号楼6层机构办公室\",\"key47\":\"0578-2780158\",\"key49\":\"0578-2780158/0578-2780278\",\"key50\":\"lslcsy@126.com\"},\"pharmacyData\":{\"cellStyleMap\":{}},\"smoAndCroPlan\":{\"cellStyleMap\":{},\"smoAndCroList\":[]}}', NULL, NULL, 1, 1564022105348, 10010, 1583919483689, 0, '', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `t_hospital` VALUES (100344, NULL, NULL, 1001, 1009, '丽水市中心医院', NULL, '丽水市中心医院（浙江大学丽水医院、温州医科大学附属第五医院）1971年筹建，1973年开诊，是国家三级甲等综合性医院、区域性标杆医院。医院职工3000余人，其中高中级职称1000余人。开放床位1630张，51个临床专科，100多个特色专科、专家门诊，2018年门急诊病人175.76万人次，出院病人7.3万人次，手术量5.54万台次。医院拥有CT 4台（西门子Force开源CT、飞利浦ICT等）、磁共振3台（西门子Aera1.5T超导型磁共振、PHILIPS 3.0T lngenia MRI等）、数字减影心血管造影机3台，以及PET-CT、ECT、直线加速器、飞秒激光等200余套先进医疗设备，其中Force开源CT、3.0T磁共振、PET-CT均为业内先进设备。　　', '', '2285888', NULL, NULL, 'http://www.lshospital.zj.cn/', NULL, '10100000', '10330000', '10331100', '浙江省丽水市括苍路289号', '', '323000', 0, 0, '{\"ethicsData\":{\"cellStyleMap\":{},\"contacts\":[],\"key1\":\"0578-2285719\",\"key2\":\"0578-2285719\",\"key3\":\"ec_lshospital@163.com\",\"key5\":\"丽水市括苍路289号丽水市中心医院（原丽水卫校内）\",\"key8\":\"法定工作日（ 8:00-11:30 13:30-17:00）\"},\"geneticsOfficeData\":{\"cellStyleMap\":{},\"contacts\":[]},\"instructionData\":{\"cellStyleMap\":{},\"contacts\":[],\"key4\":\"lsszxyygcp@163.com\",\"key49\":\"0578-2285717\"},\"pharmacyData\":{\"cellStyleMap\":{}},\"smoAndCroPlan\":{\"cellStyleMap\":{},\"smoAndCroList\":[]}}', NULL, NULL, 1, 1564022105385, 10010, 1583919480801, 0, '', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
