INSERT INTO `t_hospital` VALUES (100385, NULL, NULL, 1001, 1009, '南昌市第三医院', 'THE THIRD HOSPITAL OF NCHANG', '   南昌市第三医院始建于1917年，前身是法国教会医院——圣类思医院。2008年增挂“江西乳腺专科医院”，2016年增挂“南昌市妇幼保健院”。一个世纪以来，经过数代人的不懈努力，医院现已发展成一所以乳腺疾病专业学部为品牌，妇幼健康诊疗体系为特色，综合学科群为重点，医技学科群为支撑，集医疗、教学、科研、预防、康复、保健于一体的三级甲等综合医院。  ', '', '86-0791-86615534', NULL, NULL, 'http://www.ncsyy.com/index.php', NULL, '10100000', '10360000', '10360100', '南昌市象山南路2号', '', NULL, 0, 0, '{\"ethicsData\":{\"cellStyleMap\":{},\"contacts\":[],\"key1\":\"0791-86615653\",\"key2\":\"0791-86615653\",\"key3\":\"ncsygcplunli@126.com\",\"key5\":\"南昌市象山南路2号 行政1号楼GCP办公室\",\"key8\":\"周一至周五8:00-12:00 14:30-17:30\"},\"geneticsOfficeData\":{\"cellStyleMap\":{},\"contacts\":[]},\"instructionData\":{\"cellStyleMap\":{},\"contacts\":[],\"key4\":\"周一至周五8:00-12:00 14:30-17:30\",\"key46\":\"南昌市象山南路2号行政1号楼2楼\",\"key47\":\"0791-86615653\",\"key49\":\"0791-86615653\",\"key50\":\"sanyuangcp@163.com\"},\"pharmacyData\":{\"cellStyleMap\":{}},\"smoAndCroPlan\":{\"cellStyleMap\":{},\"smoAndCroList\":[]}}', NULL, NULL, 1, 1564022106513, 10010, 1583919286744, 0, '', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `t_hospital` VALUES (100386, NULL, NULL, 1001, 1009, '南昌市第一医院', NULL, '南昌大学第三附属医院（南昌市第一医院）是一所集医疗、教学、科研、预防保健于一体的南昌市市属最大的综合性三级甲等医院，是江西省最早的4所三级甲等医院之一。\n\n医院历史悠久，肇始于1934年,前身为江西省医务所。1949年5月，医院由解放军军代表接管，更名为“南昌市立人民医院”,医院设医务室和事务室，当时病床仅有24张，医生仅7人。\n\n1950年10月住院部落成，设病床100张，设立内科、普外科、骨科、五官科、妇产科。', '', '投诉：0791-88862216\n预约：0791-86761310       ', NULL, NULL, 'http://www.ncsdyyy.com/', NULL, '10100000', '10360000', '10360100', '象山北路128号', '', NULL, 0, 0, '{\"ethicsData\":{\"cellStyleMap\":{},\"contacts\":[],\"key1\":\"0791-86720951\",\"key3\":\"ncll2012@163.com\",\"key7\":\"http://www.ncsdyyy.com/list39.htm\",\"key8\":\"每周一、四下午为申办方提交材料等事宜接洽日\"},\"geneticsOfficeData\":{\"cellStyleMap\":{},\"contacts\":[]},\"instructionData\":{\"cellStyleMap\":{},\"contacts\":[],\"key4\":\"正常上班时间\",\"key46\":\"南昌市象山北路128号南昌市第一医院食堂楼5楼GCP机构办\",\"key47\":\"0791-88867513\",\"key49\":\"0791-88867513\",\"key50\":\"ncgcp2012@163.com\"},\"pharmacyData\":{\"cellStyleMap\":{}},\"smoAndCroPlan\":{\"cellStyleMap\":{},\"smoAndCroList\":[]}}', NULL, NULL, 1, 1564022106540, 10010, 1583919284682, 0, '', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `t_hospital` VALUES (100387, NULL, NULL, 1002, 1009, '南昌市洪都中医院', 'NCHANG HONGDU HOSPITAL OF TCM', '   南昌市洪都中医院创建于1957年，是一所集医疗、教学、科研、预防、保健为一体的三级甲等中医医院；为江西中医药大学附属洪都中医院、国家地市级重点中医院、国家药物临床试验机构、江西省特色中医院、国家重点制剂能力建设单位，入选国家中医药传承创新项目工程储备库；是国家级中医住院医师规范化培训基地、首批国家级全科医生规范化培训基地、国家中医优势学科继续教育基地、江西中医药大学与福建中医药大学等中医药高等院校指定教学医院；是江西省各类医保、工伤定点医疗医院。', '', '86-0791-86781658', NULL, NULL, 'http://www.ncshdzyy.com/', NULL, '10100000', '10360000', '10360100', '东湖区民德路264 号', '', NULL, 0, 0, '{\"ethicsData\":{\"cellStyleMap\":{},\"contacts\":[],\"key1\":\"0791-83852167\",\"key3\":\"hongduiec@163.com\",\"key5\":\"南昌市洪都中医院（北院）行政楼三楼\"},\"geneticsOfficeData\":{\"cellStyleMap\":{},\"contacts\":[]},\"instructionData\":{\"cellStyleMap\":{},\"contacts\":[],\"key46\":\"南昌市红谷滩新区碟子湖大道1399号南昌市洪都中医院北院门诊2楼\",\"key47\":\"0791-83950790\",\"key49\":\"0791-83950790\",\"key50\":\"Hdzyygcp@163.com\"},\"pharmacyData\":{\"cellStyleMap\":{}},\"smoAndCroPlan\":{\"cellStyleMap\":{},\"smoAndCroList\":[]}}', NULL, NULL, 1, 1564022106565, 10010, 1583919286246, 0, '', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
