INSERT INTO `t_hospital` VALUES (100391, NULL, NULL, 1001, 1009, '南华大学附属第二医院', 'THE SECONDHOSPITAL UNIVERSITY OF SOUTH CHI', '南华大学附属第二医院始建于1949年，原名衡阳市立人民医院，其后曾改名为衡阳市立第一人民医院、衡阳医专附属医院、衡阳医学院附属医院、2000年更名为南华大学附属第二医院，是湖南省继湘雅附一、附二医院之后的第三家教学医院，是一所集医疗、教学、科研为一体的大型综合性三级甲等医院，是省、市、县职工及居民医保、工伤保险、铁路医保、新型农村合作医疗定点医院，是全国百姓放心医院。经过70年的发展，现已形成新院、东院（胸心医学中心）和一所股份制生殖医院的格局。', '', '86-0734-8288999', NULL, NULL, 'http://www.nh2h.com/', NULL, '10100000', '10430000', '10430400', '湖南省衡阳市蒸湘区解放大道35号', '', NULL, 0, 0, '{\"ethicsData\":{\"cellStyleMap\":{},\"contacts\":[{\"cellStyleMap\":{},\"email\":\"1425271618@qq.com\",\"name\":\"何振华\",\"title\":\"伦理委员会主任\"},{\"cellStyleMap\":{},\"email\":\"353453131@qq.com；13787349441@139.com\",\"mobilePhone\":\"13787349441\",\"name\":\"谷朝晖\",\"title\":\"伦理秘书\"}],\"key17\":\"会议审查Ⅱ-Ⅳ期4400元\",\"key2\":\"0734-8899354\",\"key21\":\"快审2000元\",\"key3\":\"nhfegcpll@163.com，nhfyllwyh@163.com\",\"key5\":\"湖南省衡阳市华新开发区解放大道35号南华大学附属第二医院行政楼7楼707房\",\"key8\":\"工作日（8:00-12:00 /14:30-17:30）\",\"key9\":\"视项目情况而定\"},\"geneticsOfficeData\":{\"cellStyleMap\":{},\"contacts\":[]},\"instructionData\":{\"cellStyleMap\":{},\"contacts\":[{\"cellStyleMap\":{},\"name\":\"李荣\",\"title\":\"机构办主任\"},{\"cellStyleMap\":{},\"name\":\"匡双玉\",\"title\":\"机构办副主任\"},{\"cellStyleMap\":{},\"name\":\"游利江\",\"title\":\"项目管理员\"}],\"key4\":\"工作日（8:00-12:00 /14:30-17:30）\",\"key46\":\"湖南省衡阳市蒸湘区华新开发区解放大道35号（新院）；湖南省衡阳市石鼓区解放路30号（东院）\",\"key47\":\"0734-8899736\",\"key49\":\"0734-8899736\",\"key50\":\"nhfegcp@126.com\"},\"pharmacyData\":{\"cellStyleMap\":{}},\"smoAndCroPlan\":{\"cellStyleMap\":{},\"smoAndCroList\":[]}}', NULL, NULL, 1, 1564022106698, 10010, 1583919227790, 0, '', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `t_hospital` VALUES (100392, NULL, NULL, 1001, 1009, '南华大学附属第一医院', 'The First Affiliated Hospital Of University Of South Chi', '南华大学附属第一医院建于1943年，是国家卫生部首批授予的三级甲等医院，是一所集医疗、教学、科研、预防、康复于一体的湖南省厅直属大型综合性附属医院，同时也是国家心血管疑难病症诊治能力提升工程建设项目单位、首批国家临床教学培训示范中心、国家第二批专科规范化培训基地、国家高级卒中中心、国家代谢性疾病临床医学研究中心核心成员单位、中国创伤救治联盟成员单位、中国心力衰竭促进和COE项目合作单位、国家心力衰竭医联体湖南省省中心单位、湖南省胸痛中心、省胃癌研究中心和省脊柱微创诊疗临床示范基地。\n\n医院坐落在区域性综合交通枢纽——湖南省衡阳市，占地面积107亩，在职职工2725人，编制床位2315张（截止至2019.7.24），现设有43个临床科室和8个医技科室。', '', '86-0734-8279088//86-0734-8279087', NULL, NULL, 'http://www.nhfyyy.com/', NULL, '10100000', '10430000', '10430400', '湖南省衡阳市船山路69号', '', NULL, 0, 0, '{\"ethicsData\":{\"cellStyleMap\":{},\"contacts\":[{\"cellStyleMap\":{},\"email\":\"wwj1202@hotmail.com\",\"name\":\"王文军\",\"telePhone\":\"0734-8279153\",\"title\":\"主席/主任\"},{\"cellStyleMap\":{},\"email\":\"nhfyllwyh@163.com\",\"mobilePhone\":\"18975440900\",\"name\":\"乃海燕\",\"title\":\"秘书\"}],\"key1\":\"0734-8578934\",\"key10\":\"每月一次\",\"key2\":\"0734-8578934\",\"key3\":\"nhfyllwyh@163.com\",\"key5\":\"湖南省衡阳市船山路69号门诊大楼11层C区C-1124\",\"key8\":\"周一至周五（8:00-12:00 /夏14:30-17:30冬15:00-18:00） 周六上午8:00-12:00\",\"key9\":\"每月中旬\"},\"geneticsOfficeData\":{\"cellStyleMap\":{},\"contacts\":[]},\"instructionData\":{\"cellStyleMap\":{},\"contacts\":[{\"cellStyleMap\":{},\"email\":\"nhfygcp@163.com\",\"name\":\"邓丽菁\",\"telePhone\":\"0734-8578934\",\"title\":\"机构办秘书\"},{\"cellStyleMap\":{},\"email\":\"nhfygcp@163.com\",\"name\":\"付成效\",\"telePhone\":\"0734-8578934\",\"title\":\"机构办老师\"}],\"key4\":\"周一至周五（8:00-12:00 /夏14:30-17:30冬15:00-18:00） 周六上午8:00-12:00\",\"key46\":\"湖南省衡阳市船山路69号南华大学附属第一医院门诊11楼C区\",\"key47\":\"0734-8578930\",\"key49\":\"0734-8578934；0734-8578930\",\"key50\":\"nhfygcp@163.com\"},\"pharmacyData\":{\"cellStyleMap\":{}},\"smoAndCroPlan\":{\"cellStyleMap\":{},\"smoAndCroList\":[]}}', NULL, NULL, 1, 1564022106726, 10010, 1583919224683, 0, '', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
