INSERT INTO `t_hospital` VALUES (100395, NULL, NULL, 1001, 1009, '南京鼓楼医院', 'Nanjing Drum Tower Hospital', 'https://www.njglyy.com/about/about.asp', '', '025-83106666', NULL, NULL, 'http://www.njglyy.com/', '南京鼓楼医院', '10100000', '10320000', '10320100', '南京市中山路321号', '', NULL, 0, 0, '{\"ethicsData\":{\"cellStyleMap\":{},\"contacts\":[{\"cellStyleMap\":{},\"email\":\"gyethics@163.com \",\"name\":\"姜梅玲\",\"telePhone\":\"025-66056260\",\"title\":\"伦理秘书\"},{\"cellStyleMap\":{},\"email\":\"gyethics@163.com \",\"name\":\"戴红阳\",\"telePhone\":\"025-66056260\",\"title\":\"伦理秘书\"},{\"cellStyleMap\":{},\"email\":\"gyethics@163.com \",\"name\":\"沙莉莉\",\"telePhone\":\"025-66056260\",\"title\":\"伦理秘书\"}],\"key1\":\"025-66056260\",\"key10\":\"1次/月，一般为每月下旬，1-10日上传系统所申请的项目文件\",\"key13\":\"与伦理办公室联系分配账号后进入伦理审查管理系统填报项目资料：\\n院外请登录http://218.94.114.56:8899/\\n院内请登录http://11.1.123.56:8899/\\n纸质材料包括研究者签字的系统受理通知书、伦理审查申请表、资料完整版，盖章要求为封面+骑缝。\\n上会的13份简版材料（国家批件、组长单位伦理批件、临床方案、知情同意书、招募广告、保险证明）；PPT内容要求控制在8分钟以内，页幅不超过20张，主要包括研究目的、研究背景、研究方法、入排标准、研究过程、受益和风险等。\",\"key16\":\"是\",\"key17\":\"组长单位5000元/项，参加单位4000元/项，修正审查2000元/项\",\"key18\":\"7个工作日\",\"key3\":\"gyethics@163.com\",\"key32\":\"微信公众号有详细信息\",\"key5\":\"2号楼9楼 \",\"key6\":\"210008\",\"key7\":\"http://www.jsph.org.cn/jiaoxuekeyan/keyan/yixuelunliweiyuanhui/\",\"key8\":\"周一至周五 \",\"key9\":\"伦理上会请联系科技处伦理办公室，与伦理戴红阳老师联系后将伦理材料的电子版发送到伦理办公室邮箱，gyethics@163.com。伦理委员会邮寄地址如下：南京鼓楼医院科技处伦理办公室（戴红阳老师  电话025-66056260-20923）\"},\"geneticsOfficeData\":{\"cellStyleMap\":{},\"contacts\":[{\"cellStyleMap\":{},\"email\":\"njglyygcp@163.com\"}],\"key1\":\"是\",\"key2\":\"是\",\"key3\":\"https://njglyygcp.wetrial.com/Portal/Common/DownLoad/?tenantType=CTS_Common_Organizations_MediaTenants_StudySiteHomeOpen&tenantId=502303084504718344&folderId=578039157526865773&oid=502303084504718344&did=&canUp=true&MenuId=502304878112311552\",\"key6\":\"2周\",\"key8\":\"承诺书递交后请勿打电话来咨询是否签好，自己派人来文件盒查找即可。\"},\"instructionData\":{\"cellStyleMap\":{},\"contacts\":[{\"cellStyleMap\":{},\"name\":\"韩光曙\",\"title\":\"机构主任\"},{\"cellStyleMap\":{},\"name\":\"孙凌云\",\"title\":\"机构副主任\"},{\"cellStyleMap\":{},\"name\":\"王健\",\"title\":\"GCP办公室主任\"},{\"cellStyleMap\":{},\"email\":\"njglyygcp@163.com \",\"name\":\"戴建\",\"position\":\"1.根据网站要求将电子版的资料（电子版方案、企业法人营业执照和药物生产企业合格证、药检报告（试验药和对照药）或器械的产品自测标准及检测证明、试验批件等）上传至我院临床试验管理系统http://njglyygcp.wetrial.com/。上传前后无需电话通知，机构办公室会根据系统中的电子资料与专业项目负责人、专业主任进行沟通，确定新项目是否承接，并在系统中反馈。\\n2.肿瘤科、呼吸科项目的院内启动会召开、质控、SAE接收、项目文件接受和归档\\n3.机构及各专业的管理制度和SOP的起草及修订\",\"telePhone\":\"025- 83105910 \",\"title\":\"GCP办公室秘书\"},{\"cellStyleMap\":{},\"email\":\"njglyygcp@163.com \",\"name\":\"朱静静\",\"position\":\"1.人遗办的申报、资料审核、盖章 \\n2.心脏科、泌尿外科、男科、普外科、血管外科、神经内科以及非专业项目的的院内启动会召开、质控、SAE接收、项目文件接受和归档\\n3. 临床试验项目及SAE的汇总\",\"telePhone\":\"025- 83105910 \",\"title\":\"项目质控员\"},{\"cellStyleMap\":{},\"email\":\"njglyygcp@163.com \",\"name\":\"朱晓芳\",\"position\":\"1.妇产科、消化科、免疫科、骨科、血液科、中医科、感染科、烧伤整形科、诊断试剂项目的院内启动会召开、质控、SAE接收、项目文件接受和归档\\n2. GCP培训\\n3. CRC管理、考核与培训、CRC协议和科研协议\",\"telePhone\":\"025- 83105910 \",\"title\":\"项目质控员\"},{\"cellStyleMap\":{},\"email\":\"njglyygcp@163.com \",\"name\":\"刘灵晔\",\"position\":\"1.临床试验项目立项时的接洽、联系、电子资料审核、立项申请表的接收。根据网站要求将电子版的资料（试验方案、研究者手册、病例报告表、知情同意书、国家药品监督管理局批件等）上传至我院临床试验管理系统http://njglyygcp.wetrial.com/。上传前后无需电话通知，机构办公室会根据系统中的电子资料与专业项目负责人、专业主任进行沟通，确定新项目是否承接，并在系统中反馈。\\n2.肿瘤科、呼吸科项目的院内启动会召开、质控、SAE接收、项目文件接受和归档。\",\"telePhone\":\"025- 83105910 \",\"title\":\"项目质控员\"},{\"cellStyleMap\":{},\"email\":\"glyygcpdrug@163.com   \\n\",\"name\":\"王璐璐  \",\"position\":\"临床试验药物的接受、保存、分发、回收。\",\"telePhone\":\"025- 83106666转60020\",\"title\":\"药物管理员\"},{\"cellStyleMap\":{},\"email\":\"glyygcpdrug@163.com   \\n\",\"name\":\"朱青\",\"telePhone\":\"025- 83106666转60020\",\"title\":\"药物管理员\"},{\"cellStyleMap\":{},\"email\":\"njglyygcp@163.com \",\"name\":\"鲁萌\",\"position\":\"1. 临床试验主协议和补充协议的接洽、联系、审核\\n2.财务经费的审核、报销\\n3.内分泌科项目的院内启动会召开、质控、SAE接收、项目文件接受和归档\",\"telePhone\":\"025- 83105910 \",\"title\":\"资料管理员\"}],\"key1\":\"https://njglyygcp.wetrial.com\",\"key10\":\"是\",\"key11\":\"是\",\"key14\":\"WeTrial系统申请\",\"key16\":\"WeTrial系统申请\",\"key17\":\" 新临床试验项目的具体接洽流程：根据网站要求将电子版的资料（电子版方案、企业法人营业执照和药物生产企业合格证、药检报告（试验药和对照药）或器械的产品自测标准及检测证明、试验批件等）上传至我院临床试验管理系统http://njglyygcp.wetrial.com/，发送资料前后均无需来电告知。机构办公室会根据系统中的电子资料与专业项目负责人、专业主任进行沟通，确定新项目是否承接，并在系统中反馈。\",\"key2\":\"鼓楼医院临床试验机构\",\"key26\":\"是\",\"key27\":\"新项目通过伦理审批后，申办方请参阅药物/医疗器械/诊断试剂/临床试验合同模板、检验检查费用价目表（详见下载区）草拟临床试验协议（我院暂不开具增值税发票开行政事业单位资金往来票据，暂无税费），与机构办鲁老师邮件预约(协议所有相关通知均以邮件形式，故务必邮件预约写明项目情况，无须电话或来访）合同讨论会时间(CRC协议及科研协议请与朱晓芳老师联系）。按照医院审计建议，所有合同均需参加医院审计处、财务处、机构办、纪委参加的合同讨论会，合同讨论会时间不定，一般凑齐项目即会召开，机构办会至少提前三个工作日邮件通知日程，请注意查收。合同讨论会后进行协议审核（无预审核），请与鲁老师邮件联系。通知定稿后盖申办方公章和骑缝章后交给研究者签字，研究者签字后交机构办鲁老师，进行合同签字流程。由于流程复杂，请尊重机构办人员日常工作，勿电话或邮件催促、询问进度。签署完毕后会在CRA或CRC微信群中通知，请依据专业联系质控员加入微信群，进群请改名为项目名+姓名。合同出现问题，机构办工作人员会主动联系，故递交时请做好登记。审核不通过的退回协议修改后重新递交，请附退回的审计老师修改的留痕版(原件或复印件)。补充协议请同时递交主协议复印件，方便审计老师比对。如主协议未签署，请勿递交CRC协议，CRC协议作为主协议的补充协议处理，故递交时请在首页右上角标注主协议编号（一般GYHT开头）+“补”字样。\",\"key3\":\"请依据专业联系质控员加入微信群，进群请改名为项目名+姓名\",\"key30\":\"开户名：南京鼓楼医院 \\n开户行：南京市工商银行汉口路分理处\\n帐号：4301011309001101669         \",\"key31\":\"是\",\"key4\":\"机构办公室每周星期一下午不接待来访，其他时间若需归档、来访、盖章、溯源核 查 等请电话预约来访时间\",\"key45\":\"试验结束后，监查员与研究者按照下载区《临床试验归档要求》逐条核对，按财务结算要求与鲁老师邮件预约进行结算。归档文件完整齐全后交机构档案室存档（事先联系各自质控老师），并留存5年后项目资料转存或销毁等事宜的联系人记录。\\n总结报告或分中心小结完成后，监查员到下载区下载《总结报告或分中心小结盖章前相关信息确认表》，根据表格所示流程，完成所需确认信息，全部完成后方可盖章（盖章前电话联系025-83105910/68182266)。\",\"key46\":\"2号楼9楼 \",\"key48\":\"210008\",\"key49\":\"025- 83105910 \",\"key5\":\"2017年5月18日通过国家食品药品监督管理总局组织的普通外科、妇产科、泌尿外科、心血管内科、呼吸科、肿瘤科、神经内科、内分泌科、消化科等9个专业的复核检查（证书编号XF20170525）。2017年5月18日通过国家食品药品监督管理总局组织的骨科、免疫、传染、血液内科、烧伤、整形外科、中医科（肿瘤方向）等7个专业的新增专业资格认定（证书编号763）。目前机构包括I期临床研究室和16个经国家食品药品监督管理总局批准从事药物临床试验的专业。\",\"key50\":\"njglyygcp@163.com \",\"key6\":\"是\"},\"pharmacyData\":{\"cellStyleMap\":{},\"key2\":\"是\",\"key3\":\"https://njglyygcp.wetrial.com/Portal/Common/DownLoad?oid=502303084504718344&MenuId=502304878112311552\"},\"smoAndCroPlan\":{\"cellStyleMap\":{},\"key1\":\"是\",\"key10\":\"https://njglyygcp.wetrial.com/Portal/Common/DownLoad/?tenantType=CTS_Common_Organizations_MediaTenants_StudySiteHomeOpen&tenantId=502303084504718344&folderId=628679365950144409&oid=502303084504718344&did=&canUp=true&MenuId=502304878112311552\",\"key3\":\"是\",\"key4\":\"是\",\"key5\":\"https://njglyygcp.wetrial.com/Portal/Common/DownLoad/?tenantType=CTS_Common_Organizations_MediaTenants_StudySiteHomeOpen&tenantId=502303084504718344&folderId=636563553118564599&oid=502303084504718344&did=&canUp=true&MenuId=502304878112311552\",\"smoAndCroList\":[{\"cellStyleMap\":{},\"key1\":\"联斯达\"},{\"cellStyleMap\":{},\"key1\":\"上海津石\"},{\"cellStyleMap\":{},\"key1\":\"杭州思默\"},{\"cellStyleMap\":{},\"key1\":\"普蕊斯\"},{\"cellStyleMap\":{},\"key1\":\"圣兰格\"}]}}', '123201004260904451', '南京鼓楼医院', 1, 1564022106810, 10010, 1583919257952, 0, '', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
