INSERT INTO `t_hospital` VALUES (100427, NULL, NULL, 1005, 1009, '宁波市妇女儿童医院', 'NINGBO WOMEN & CHILDREN\'S HOSPITAL', '　宁波市妇女儿童医院（宁波市妇幼保健院 宁波市儿童医学中心）是一家以妇产科、儿科为主要专科，功能完善、设施齐全、技术先进、服务规范、特色鲜明的集医疗、保健、教学、科研、预防、急救、康复、健康教育为一体的三级甲等妇幼保健院。既承担了宁波市及周边地区妇女儿童各类疾病的诊治和危重疾病的抢救工作，又承担着全市妇幼保健业务的技术指导工作。', '', '87083300', NULL, NULL, 'http://www.nbfeyy.com/', NULL, '10100000', '10330000', '10330200', '宁波市柳汀街339号', '', '315012', 0, 0, '{\"ethicsData\":{\"cellStyleMap\":{},\"contacts\":[]},\"geneticsOfficeData\":{\"cellStyleMap\":{},\"contacts\":[]},\"instructionData\":{\"cellStyleMap\":{},\"contacts\":[{\"cellStyleMap\":{},\"mobilePhone\":\"13566560090\",\"name\":\"王磊\"}],\"key46\":\"宁波市海曙区柳汀街339号\"},\"pharmacyData\":{\"cellStyleMap\":{}},\"smoAndCroPlan\":{\"cellStyleMap\":{},\"smoAndCroList\":[]}}', NULL, NULL, 1, 1564022107687, 10010, 1583919478039, 0, '', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
