INSERT INTO `t_hospital` VALUES (100452, NULL, NULL, 1001, 1009, '青岛市市立医院', 'QINGDAO MUNICIPAL HOSPITAL', '青岛市市立医院始建于1916年，是集医疗、教学、科研、保健、康复、疗养于一体的综合性三级甲等医院，是2008年北京奥运会和残奥会、2018年上海合作组织青岛峰会、2019年海军节医疗保障定点医院，青岛市属首家互联网医院，连续十年保持全国文明单位荣誉称号。学科实力位居中国医院科技量值综合评价百强榜89位、山东省住院服务绩效综合评价第9位。\n\n医院辖市立医院本部、市立医院东院、市皮肤病防治院、市北九水疗养院、徐州路院区5个院区，现有职工4159人，高级专业技术人员728名，博士251名，硕士899名。医院编制床位2713张，年门急诊量227万人次、出院病人11.4万人次、住院手术5.6万人次。\n\n', '', '0532—82716767', NULL, 'qdsslyybgs@163.com', 'http://www.qdslyy.cn/', 'qd-slyy', '10100000', '10370000', '10370200', '山东省青岛市胶州路1号', '', '266011', 0, 0, '{\"ethicsData\":{\"cellStyleMap\":{},\"contacts\":[{\"cellStyleMap\":{},\"name\":\"丁华民\",\"title\":\"主任委员\"},{\"cellStyleMap\":{},\"mobilePhone\":\"13675320681\",\"name\":\"代旭东\",\"title\":\"联系人\"},{\"cellStyleMap\":{},\"mobilePhone\":\"13675320681\",\"name\":\"刘华强\",\"title\":\"联系人\"}],\"key1\":\"0532-82789053\",\"key3\":\"qdsslyylunli@163.com\",\"key5\":\"青岛市胶州路1号青岛市市立医院办公楼5楼医学科研部\",\"key6\":\"266011\",\"key8\":\"周一到周五\"},\"geneticsOfficeData\":{\"cellStyleMap\":{},\"contacts\":[]},\"instructionData\":{\"cellStyleMap\":{},\"contacts\":[{\"cellStyleMap\":{},\"name\":\"宣世英\",\"title\":\"机构负责人\"},{\"cellStyleMap\":{},\"name\":\"谭兰\",\"title\":\"办公室主任\"},{\"cellStyleMap\":{},\"name\":\"赵艳\",\"title\":\"办公室副主任\"},{\"cellStyleMap\":{},\"name\":\"张文竹\",\"title\":\"秘书\"},{\"cellStyleMap\":{},\"name\":\"张媛媛\",\"title\":\"质控员\"},{\"cellStyleMap\":{},\"name\":\"孙韬华\",\"title\":\"项目管理员\"},{\"cellStyleMap\":{},\"name\":\"周扬\",\"title\":\"药物管理员\"}],\"key4\":\"周一到周五\",\"key46\":\"山东省青岛市城阳路5号\",\"key47\":\"0532-82789191\",\"key48\":\"266012\",\"key49\":\"0532-82789192\",\"key50\":\"gcp82789192@126.com\"},\"pharmacyData\":{\"cellStyleMap\":{}},\"smoAndCroPlan\":{\"cellStyleMap\":{},\"smoAndCroList\":[]}}', NULL, NULL, 1, 1564022108370, 10010, 1583919339054, 0, '', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `t_hospital` VALUES (100453, NULL, NULL, 1001, 1009, '青岛市中心医院', 'Qingdao Central Hospital', '青岛市中心医疗集团由青岛市中心医院、青岛市肿瘤医院、青岛市职业病防治院共同组建而成。青岛市中心医院，始建于1953年(始称青岛纺织医院)，1986年并称青岛医学院第二附属医院；1993年首批晋升为三级甲等综合医院；2001年经山东省卫生厅和市卫生局批准，更名为青岛市中心医院,并承担全市职业病防治任务；2013年3月顺利通过山东省综合医院等级（三级甲等）复审；2016年加挂青岛大学第二临床医学院。青岛市肿瘤医院，始建于1972年，是青岛市唯一一所市属肿瘤防治三级专科医院。', '', '0532-84961699', '0532-84863506', NULL, 'http://www.qdzxyy.com/', 'qdzxyy', '10100000', '10370000', '10370200', '山东省青岛市市北区四流南路127号', NULL, '266042', 0, 0, '{\"ethicsData\":{\"cellStyleMap\":{},\"contacts\":[{\"cellStyleMap\":{},\"name\":\"刘雪丽\",\"title\":\"医学院秘书\"}],\"key1\":\"0532-84961231,0532-84850840\",\"key3\":\"qdzxec@163.com\",\"key5\":\"山东省青岛市市北区四流南路127号\",\"key6\":\"266042\"},\"geneticsOfficeData\":{\"cellStyleMap\":{},\"contacts\":[]},\"instructionData\":{\"cellStyleMap\":{},\"contacts\":[{\"cellStyleMap\":{},\"name\":\"周学锋\",\"telePhone\":\"0532-84961231\",\"title\":\"机构办公室主任\"},{\"cellStyleMap\":{},\"name\":\"杨海\",\"telePhone\":\"0532-84961431\",\"title\":\"机构办公室秘书\"}],\"key1\":\"http://www.qdzxyy.com/ywlcsy/about.aspx\",\"key46\":\"山东省青岛市市北区四流南路127号\",\"key48\":\"266042\",\"key49\":\"0532-84855019\",\"key50\":\"qdzxgcp@163.com\"},\"pharmacyData\":{\"cellStyleMap\":{}},\"smoAndCroPlan\":{\"cellStyleMap\":{},\"smoAndCroList\":[]}}', '123702004274024990', '兰克涛', 1, 1564022108397, 10010, 1583919342630, 0, '', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
