INSERT INTO `t_hospital` VALUES (100458, NULL, NULL, 1001, 1009, '衢州市人民医院', 'People\'s Hospital of Quzhou', '衢州市人民医院是一所三级甲等综合性医院，始建于1948年，前身为浙江省立衢州医院。2005年增冠衢州中心医院院名，2008年成为浙江中医药大学附属医院，2014年成为浙江大学衢州医院，解放军301、307医院的协作医院。衢州市临床、放射、检验、病理、麻醉、药事等十七个质控中心及市心脑血管病、糖尿病防治中心（办公室）、肿瘤中心、妇科微创中心、耳鼻咽喉疾病诊治中心均设在本院，是本地区及闽、赣、皖边际区域临床医疗、急诊急救、临床教学、临床科研和保健康复的中心。', '', '0570-8895120', NULL, NULL, 'http://www.qzhospital.com/', NULL, '10100000', '10330000', '10330800', '浙江省衢州市柯城区钟楼底2号', '', '324000', 0, 0, '{\"ethicsData\":{\"cellStyleMap\":{},\"contacts\":[],\"key1\":\"0570-3056706，0570-3056786\",\"key3\":\"yj411@126.com\"},\"geneticsOfficeData\":{\"cellStyleMap\":{},\"contacts\":[]},\"instructionData\":{\"cellStyleMap\":{},\"contacts\":[],\"key46\":\"浙江省衢州市柯城区钟楼底2号\",\"key49\":\"0570-3055197，15067016098\",\"key50\":\"qzryjgb@126.com\"},\"pharmacyData\":{\"cellStyleMap\":{}},\"smoAndCroPlan\":{\"cellStyleMap\":{},\"smoAndCroList\":[]}}', NULL, NULL, 1, 1564022108534, 10010, 1583919480435, 0, '', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
