INSERT INTO `t_hospital` VALUES (100470, NULL, NULL, 1001, 1009, '山东大学第二医院', 'THE SECOND HOSPITAL OF SHANDONG UNIVERITY', '山东大学第二医院座落于历史文化名城泉城济南北部，是国家卫生健康委员会委属（管）医院，百年名校山东大学直属医院。医院秉承齐鲁医科“博施济众 广智求真”血脉基因，遵循“明德至善 护佑民生”院训精神，开拓创新，追求卓越，2011年成为全国第一家通过新版等级评审标准的三甲医院，2017年又成为山东省第一家通过国家等级复审的医院，标准化示范引领逐步彰显，“最安全”“最有效”服务体系日臻完善。目前，医院事业不断拓展，形成了中心院区、南部院区、山东大学校医院、招远分院、善德公司及医联体组成的发展格局。积极投身医药卫生体制改革，托管运营济南善德养老院，国内率先提出“医养结合”理念，获评山东省首批医养结合示范单位；与玲珑集团共同建设山东省第一家民营三级综合医院，成为胶东地区一支重要的医疗力量；加强医联体建设，先后与40余家基层医疗机构开展帮扶和合作，托管山东大学校医院，发挥社区卫生服务中心作用，促进优质资源下沉。\n\n\n', '', '0531-88197777', NULL, NULL, 'http://www.sdey.net/', 'shandaeryuan', '10100000', '10370000', '10370100', '山东省济南市北园大街247号', NULL, NULL, 0, 0, '{\"ethicsData\":{\"cellStyleMap\":{},\"contacts\":[{\"cellStyleMap\":{},\"name\":\"张晗\",\"title\":\"主任委员\"},{\"cellStyleMap\":{},\"name\":\"孙琳\",\"title\":\"副主任委员\"},{\"cellStyleMap\":{},\"name\":\"苗龙星\",\"title\":\"秘书\"}],\"key1\":\"0531-85875203,0531-85875756,0531-85875761\"},\"geneticsOfficeData\":{\"cellStyleMap\":{},\"contacts\":[]},\"instructionData\":{\"cellStyleMap\":{},\"contacts\":[{\"cellStyleMap\":{},\"email\":\"haisheng1008@126.com\",\"name\":\"王海生\",\"title\":\"机构办主任\"},{\"cellStyleMap\":{},\"email\":\"ck1319@163.com\",\"name\":\"常堃\",\"title\":\"机构办秘书\"}],\"key1\":\"http://lcylb.sdey.net/\",\"key46\":\"山东省济南市天桥区北园大街247号山大二院办公楼四楼药物临床试验机构办公\",\"key47\":\"0531-85875203\",\"key49\":\"0531-85875203,0531-85875330\",\"key50\":\"GCP0531@126.com\"},\"pharmacyData\":{\"cellStyleMap\":{}},\"smoAndCroPlan\":{\"cellStyleMap\":{},\"smoAndCroList\":[]}}', '12370000495573096D', '王传新', 1, 1564022108862, 10010, 1583919335507, 0, '', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
