INSERT INTO `t_hospital` VALUES (100472, NULL, NULL, 1001, 1009, '山东大学齐鲁医院', 'QILU HOSPITAL OF SHANDONG UNIVERSITY', '山东大学齐鲁医院位于山东省济南市文化西路107号、山东省济南市高新区天辰路2000号，始建于公元1890年，是一所三级综合医院，是济南市医保定点医院。', '', '0531-82166666', NULL, 'qiluhospital@sina.com', 'http://www.qiluhospital.com/', 'qlyywx', '10100000', '10370000', '10370100', '山东省济南市文化西路107号', NULL, '250012', 0, 0, '{\"ethicsData\":{\"cellStyleMap\":{},\"contacts\":[{\"cellStyleMap\":{},\"name\":\"医学伦理\",\"telePhone\":\"0531-82169603\"}],\"key1\":\"0531-82169603\",\"key10\":\"2次/月\",\"key5\":\"山东省济南市文化西路107号山东大学齐鲁医院和平楼\",\"key6\":\"250012\",\"key8\":\"周一至周五\"},\"geneticsOfficeData\":{\"cellStyleMap\":{},\"contacts\":[]},\"instructionData\":{\"cellStyleMap\":{},\"contacts\":[{\"cellStyleMap\":{},\"name\":\"陈玉国\",\"title\":\"机构主任\"},{\"cellStyleMap\":{},\"email\":\"gcp82169161@sina.com\",\"name\":\"王白璐\",\"telePhone\":\"0531-82169602\",\"title\":\"机构办主任\"},{\"cellStyleMap\":{},\"email\":\"zhaoshiliang208@163.com\",\"name\":\"赵时亮\",\"title\":\"机构办秘书\"},{\"cellStyleMap\":{},\"name\":\"麻立丽\",\"title\":\"质控员\"},{\"cellStyleMap\":{},\"name\":\"姜波\",\"title\":\"质控员\\\\项目管理员\"},{\"cellStyleMap\":{},\"name\":\"刘娜\",\"title\":\"质控员\\\\药物管理员\"}],\"key4\":\"周一至周五\",\"key46\":\"山东省济南市文化西路107号山东大学齐鲁医院和平楼一层\",\"key47\":\"0531-86926068\",\"key48\":\"250012\",\"key49\":\"0531-82169602,0531-82169161\",\"key50\":\"gcp82169161@sina.com\"},\"pharmacyData\":{\"cellStyleMap\":{}},\"smoAndCroPlan\":{\"cellStyleMap\":{},\"smoAndCroList\":[]}}', '12370000495574910W', '陈玉国', 1, 1564022108916, 10010, 1583919345559, 0, '', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `t_hospital` VALUES (100473, NULL, NULL, 1005, 1009, '山东省精神卫生中心', NULL, '山东省精神卫生中心坐落于泉城济南风景秀丽的燕子山北麓，占地面积5.3万平方米，建筑面积4.6万余平方米。院内花木飘香，四季常青，环境幽雅，现代化的病房大楼宏伟壮观，格外引人注目。2015年7月被评为三级甲等精神卫生专科医院，2019年11月，顺利通过三甲复审。 中心前身为山东省精神病医院，始建于1954年9月，1956年6月正式收治病人，1992年更名为山东省精神卫生中心，是山东省卫生健康委员会直属单位，同时是山东省心理咨询中心、山东大学和济宁医学院附属精神卫生中心、山东省药物依赖治疗中心、山东省精神医学研究所、山东省精神疾病司法鉴定所、山东省药物滥用监测中心、国家药物临床试验机构、省级药品不良反应检测工作站、山东省吸毒成瘾认定戒毒机构。承担着全省精神卫生的医疗、教学、科研、社会社防、康复、司法鉴定、物质依赖、心理咨询、对外学术交流和基层精神科专业人员的培训任务。是全省唯一一家省级精神疾病专科医院，全省医保转诊专科医院及济南市首批医保定点医院。', '', '0531-88933887', NULL, NULL, 'http://www.sdmhc.com/', 'sdsjswszx', '10100000', '10370000', '10370100', '山东省济南市文化东路49号', '', '250014', 0, 0, '{\"ethicsData\":{\"cellStyleMap\":{},\"contacts\":[],\"key1\":\"0531-86336700\"},\"geneticsOfficeData\":{\"cellStyleMap\":{},\"contacts\":[]},\"instructionData\":{\"cellStyleMap\":{},\"contacts\":[],\"key46\":\"山东省济南市文化东路49号\",\"key48\":\"250014\",\"key49\":\"0531-86336728\",\"key50\":\"sdsjwywk2013@163.com\"},\"pharmacyData\":{\"cellStyleMap\":{}},\"smoAndCroPlan\":{\"cellStyleMap\":{},\"smoAndCroList\":[]}}', '1237000049557403XM', '王汝展 ', 1, 1564022108945, 10010, 1583919345810, 0, '', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `t_hospital` VALUES (100474, NULL, NULL, 1001, 1009, '山东省立医院', 'Shandong Provincial Hospital', '　山东省立医院始建于1897年，迄今已有122年历史。现已发展成为集医疗、科研、教学、预防保健、指导基层为一体的大型综合性三级甲等公立医院，有中心院区和东院两个院区，年门急诊总量404万人次，年出院患者15.86万人次，手术8.51万台次，医疗综合服务能力全省第一。2018年，医院在复旦大学中国医院排行榜位列第46，华东区综合实力位列第14;“中国顶级医院100强”位列第28。', '', '0531-87938911', NULL, NULL, 'http://www.sph.com.cn/', 'gh_b4268925edf4', '10100000', '10370000', '10370100', '山东省济南市经五纬七路324号', NULL, '250021', 0, 0, '{\"ethicsData\":{\"cellStyleMap\":{},\"contacts\":[{\"cellStyleMap\":{},\"name\":\"王玉林\",\"title\":\"主任委员\"},{\"cellStyleMap\":{},\"name\":\"董庆\",\"title\":\"副主任委员\"},{\"cellStyleMap\":{},\"name\":\"周小明\",\"title\":\"秘书\"}],\"key1\":\"0531-68776009\",\"key2\":\"0531-87900927\"},\"geneticsOfficeData\":{\"cellStyleMap\":{},\"contacts\":[]},\"instructionData\":{\"cellStyleMap\":{},\"contacts\":[],\"key46\":\"山东省济南市经五纬七路324号\",\"key48\":\"250021\",\"key49\":\"0531-87900927,0531－85186009\",\"key50\":\"shiyanjigouban@126.com\"},\"pharmacyData\":{\"cellStyleMap\":{}},\"smoAndCroPlan\":{\"cellStyleMap\":{},\"smoAndCroList\":[]}}', '12370000495571840L', '赵升田 ', 1, 1564022108970, 10010, 1583919345731, 0, '', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
