INSERT INTO `t_hospital` VALUES (100478, NULL, NULL, 1001, 1009, '山东省胸科医院', 'SHANDONG PROVINCIAL CHEST HOSPITAL', '山东省胸科医院隶属于山东省卫生健康委员会，始建于1949年，历经变迁，逐渐发展为一所集医疗、教学、科研、预防、保健功能为一体的新型专科医院。医院同时也是山东大学附属山东省胸科医院、山东省结核病与呼吸病防治研究中心、山东省公共卫生医疗救治中心，还获批成为山东省肺结节诊疗中心、山东省省直保健定点医院、国家呼吸临床研究中心中日医院呼吸专科医联体单位、中国防痨协会结核病转化医学创新基地、中国医师协会呼吸内镜医师培训基地、山东省红十字应急救护培训基地等。', '', '0531-86952696', NULL, NULL, 'http://www.xkyy.org.cn/', 'sdsxkyy1949', '10100000', '10370000', '10370100', '山东省济南市历山路46号', '', '250013', 0, 0, '{\"ethicsData\":{\"cellStyleMap\":{},\"contacts\":[]},\"geneticsOfficeData\":{\"cellStyleMap\":{},\"contacts\":[]},\"instructionData\":{\"cellStyleMap\":{},\"contacts\":[],\"key46\":\"山东省济南市历山路46号\",\"key48\":\"250013\",\"key49\":\"0531-86568182,0531-86568187\",\"key50\":\"kejiao6@126.com\"},\"pharmacyData\":{\"cellStyleMap\":{}},\"smoAndCroPlan\":{\"cellStyleMap\":{},\"smoAndCroList\":[]}}', ' 12370000495540729A', '秦敬民 ', 1, 1564022109081, 10010, 1583919345456, 0, '', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
