INSERT INTO `t_hospital` VALUES (100483, NULL, NULL, 1002, 1009, '山东中医药大学附属医院', NULL, '青岛市市立医院始建于1916年，是集医疗、教学、科研、保健、康复、疗养于一体的综合性三级甲等医院，是2008年北京奥运会和残奥会、2018年上海合作组织青岛峰会、2019年海军节医疗保障定点医院，青岛市属首家互联网医院，连续十年保持全国文明单位荣誉称号。学科实力位居中国医院科技量值综合评价百强榜89位、山东省住院服务绩效综合评价第9位。\n\n医院辖市立医院本部、市立医院东院、市皮肤病防治院、市北九水疗养院、徐州路院区5个院区，现有职工4159人，高级专业技术人员728名，博士251名，硕士899名。医院编制床位2713张，年门急诊量227万人次、出院病人11.4万人次、住院手术5.6万人次。\n\n', '', '0531-58675126,0531-96717120', NULL, NULL, 'http://www.sdzydfy.com/', 'sdzyydxfsyy', '10100000', '10370000', '10370100', '山东省济南市文化西路42号', '', '250011', 0, 0, '{\"ethicsData\":{\"cellStyleMap\":{},\"contacts\":[{\"cellStyleMap\":{},\"name\":\"陈柏楠\",\"title\":\"主席\"},{\"cellStyleMap\":{},\"name\":\"薛一涛\",\"title\":\"副主任\"},{\"cellStyleMap\":{},\"name\":\"李伟\",\"title\":\"副主任\"},{\"cellStyleMap\":{},\"name\":\"蔡治国\",\"title\":\"秘书\"},{\"cellStyleMap\":{},\"name\":\"张倩\",\"title\":\"秘书\"},{\"cellStyleMap\":{},\"name\":\"张立娟\",\"title\":\"秘书\"}],\"key1\":\"0531-68616733\",\"key3\":\"keyanke6236@163.com\"},\"geneticsOfficeData\":{\"cellStyleMap\":{},\"contacts\":[]},\"instructionData\":{\"cellStyleMap\":{},\"contacts\":[{\"cellStyleMap\":{},\"name\":\"孙洪胜\",\"title\":\"机构办主任\"}],\"key46\":\"济南市历下区经十路16369号 省中医东院区图书馆楼三楼\",\"key48\":\"250000\",\"key49\":\"0531-82665203\",\"key50\":\"keyanke6236@163.com\"},\"pharmacyData\":{\"cellStyleMap\":{}},\"smoAndCroPlan\":{\"cellStyleMap\":{},\"smoAndCroList\":[]}}', '12370000495570397U', '任勇 ', 1, 1564022109213, 10010, 1583919339522, 0, '', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
