INSERT INTO `t_hospital` VALUES (100488, NULL, NULL, 1001, 1009, '山西省人民医院', 'Shanxi Provincial People\'s Hospital', '山西省人民医院（山西医科大学附属人民医院）创建于1953年，1955年建成开诊，是直属于山西省卫生健康委员会的一所集医疗、教学、科研、预防、康复、保健、急救为一体的综合型三级甲等医院，是山西省最大的医疗机构之一，担负着包括急危重症在内的各种医疗救治、突发公共卫生应急和重大任务抢险以及干部保健等职能，医疗服务范围遍及全省各地及周边省份。', '', '0351-4960130 ', NULL, 'sxsyjj@126.com', 'http://www.sxsrmyy.com/index.asp', NULL, '10100000', '10140000', '10140100', '山西省太原市双塔寺街29号', NULL, '30012', 0, 0, '{\"ethicsData\":{\"cellStyleMap\":{},\"contacts\":[]},\"geneticsOfficeData\":{\"cellStyleMap\":{},\"contacts\":[]},\"instructionData\":{\"cellStyleMap\":{},\"contacts\":[{\"cellStyleMap\":{},\"name\":\"柴老师\"}],\"key46\":\"太原市双塔寺街29号\",\"key49\":\"0351-4960370\"},\"pharmacyData\":{\"cellStyleMap\":{}},\"smoAndCroPlan\":{\"cellStyleMap\":{},\"smoAndCroList\":[]}}', NULL, NULL, 1, 1564022109349, 10010, 1583919356365, 0, '', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
