INSERT INTO `t_hospital` VALUES (100490, NULL, NULL, 1001, 1009, '山西省运城市中心医院', 'Yuancheng Central Hospital', '运城古称河东，因“盐运之城”而得名，是中华文明的发祥地。位于晋、陕、豫三省交界处，黄河金三角经济圈黄金位置，这里文化积淀厚重，交通便捷，这里也是最早叫中国的地方。\n\n　　运城市中心医院创建于1947年，其前身为太岳三专署政民医院。历经70余年建设与发展，如今已成为运城市唯一一所集医疗、教学、科研、预防、保健和康复为一体的现代化三级甲等综合医院，山西医科大学直属附属医院 第八临床医学院，山西省区域医疗中心，国家住院医师规范化培训基地，全科医生临床培养基地，全国综合医院中医示范单位，国家药物临床试验机构、国家标准版的胸痛中心，高级卒中中心，全国百姓放心示范医院，我院护理部荣获“全国巾国建功先进集体”称号。', '', '86-0359-6396212，86-0359-6396999', NULL, NULL, 'http://ycch.cn/', NULL, '10100000', '10140000', '10140800', '山西省运城市河东东街3690号', NULL, NULL, 0, 0, '{\"ethicsData\":{\"cellStyleMap\":{},\"contacts\":[]},\"geneticsOfficeData\":{\"cellStyleMap\":{},\"contacts\":[]},\"instructionData\":{\"cellStyleMap\":{},\"contacts\":[],\"key49\":\"0359-6396163；0359-6397073\",\"key50\":\"linchuangyaoxue@163.com\"},\"pharmacyData\":{\"cellStyleMap\":{}},\"smoAndCroPlan\":{\"cellStyleMap\":{},\"smoAndCroList\":[]}}', NULL, NULL, 1, 1564022109404, 10010, 1583919356866, 0, '', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
