INSERT INTO `t_hospital` VALUES (100515, NULL, NULL, 1001, 1013, '上海市北站医院', NULL, '上海市静安区北站医院是一所二级综合医院，公立事业性单位，隶属于上海市闸北区卫生局，是上海市医保定点医疗机构，始建于1927年，1955年在原上海同德医学院基础上扩建而成。医院距上海新客站东侧（南星路29号）约200米。医院占地面积约五亩，共有建筑面积15700平方米。医院在科学管理、专科建设、人才培养、精神文明建设等方面取得了显著的进步，2011年被授予“上海市卫生系统文明单位”，并连续21年被授予“闸北区文明单位”的称号。', '', '021-63549199', NULL, NULL, '', '上海市北站医院', '10100000', '10310000', '10310100', '上海闸北区南星路29号', NULL, NULL, 0, 0, '{\"ethicsData\":{\"cellStyleMap\":{},\"contacts\":[]},\"geneticsOfficeData\":{\"cellStyleMap\":{},\"contacts\":[]},\"instructionData\":{\"cellStyleMap\":{},\"contacts\":[]},\"pharmacyData\":{\"cellStyleMap\":{}},\"smoAndCroPlan\":{\"cellStyleMap\":{},\"smoAndCroList\":[]}}', NULL, NULL, 1, 1564022110089, 10010, 1583919399028, 0, '', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
