INSERT INTO `t_hospital` VALUES (100547, NULL, NULL, 1001, 1009, '上饶市人民医院', 'ShangRao People\'s Hospital', ' \n　　上饶市人民医院始建于1940年，初称“江西省第六行政区中心卫生院”，解放前和建国初期称“江西省立上饶医院”，1970年改名为“江西省上饶地区人民医院”，2000年撤地设市后改称为“江西省上饶市人民医院”。经过79年的历程，现已发展为全市规模最大、技术力量最雄厚、医疗设备最先进的医疗服务中心，是一所集医疗、教学、科研、预防保健于一体的三级甲等综合医院。', '', '急救热线：0793-8100120  预约挂号：0793-8100469  医疗投诉：0793-8100315', NULL, NULL, 'http://www.srsrmyy.cn/', NULL, '10100000', '10360000', '10361100', '江西省上饶市书院路84－87号', NULL, '334000', 0, 0, '{\"ethicsData\":{\"cellStyleMap\":{},\"contacts\":[{\"cellStyleMap\":{},\"email\":\"569285739@qq.com\",\"mobilePhone\":\"13667938615\",\"name\":\"徐文君\"}],\"key1\":\"0793-8100667\",\"key2\":\"0793-8100667\",\"key5\":\"书院路86号\",\"key8\":\"周一至周五 8:00am-17:30pm（法定节假日除外）\"},\"geneticsOfficeData\":{\"cellStyleMap\":{},\"contacts\":[]},\"instructionData\":{\"cellStyleMap\":{},\"contacts\":[{\"cellStyleMap\":{},\"mobilePhone\":\"13507937399\",\"name\":\"陈丽霞\"},{\"cellStyleMap\":{},\"mobilePhone\":\"18679339978\",\"name\":\"黄素君\",\"position\":\"质量管理员\"}],\"key4\":\"周一至周五 8:00am——17:30pm（法定节假日除外）\",\"key46\":\"书院路86号\",\"key47\":\"0793-8100658\",\"key49\":\"0793-8100658\",\"key50\":\"srsrmyygcp@163.com\"},\"pharmacyData\":{\"cellStyleMap\":{}},\"smoAndCroPlan\":{\"cellStyleMap\":{},\"smoAndCroList\":[]}}', NULL, NULL, 1, 1564022110972, 10010, 1583919283249, 0, '', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
