INSERT INTO `t_hospital` VALUES (100549, NULL, NULL, 1001, 1009, '邵阳市中心医院', 'The Central Hospital of Shaoyang', '医院创建于1946年，经过半个多世纪的建设和发展，现已成为湘西南的一所集医疗、教学、科研、预防、保健为一体的地市级综合医院。1993年被卫生部首批列入全国500所大型医院，1997年晋升为国家三级甲等医院。1999年被评审通过为湖南省普通高等医学院校临床教学优秀基地，现为南华大学附属医院。2011年被卫生部确定为国家级湘西南区域医疗中心，2011至2016年连续6年强势挺进“全国最有竞争力地市级医院100强”行列。', '', '86-0739-5328175/86-0739-5323380', NULL, NULL, 'http://hnsyzxyy.com/', NULL, '10100000', '10430000', '10430500', '邵阳市红旗路乾元巷36号', NULL, '422000', 0, 0, '{\"ethicsData\":{\"cellStyleMap\":{},\"contacts\":[{\"cellStyleMap\":{},\"name\":\"刘小勇\",\"title\":\"主任\"},{\"cellStyleMap\":{},\"name\":\"张继红\",\"title\":\"伦理会秘书\"}],\"key1\":\"0739-5320314\",\"key10\":\"视项目和安排确定\",\"key2\":\"0739-5312599\",\"key21\":\"4000元\",\"key3\":\"1634103081@qq.com\",\"key5\":\"湖南省邵阳市中心医院医技大楼12楼\",\"key8\":\"周一至周五，8:00-12:00，14:30-17:30\"},\"geneticsOfficeData\":{\"cellStyleMap\":{},\"contacts\":[]},\"instructionData\":{\"cellStyleMap\":{},\"contacts\":[{\"cellStyleMap\":{},\"name\":\"\\t刘燕湘\",\"title\":\"机构办主任\"},{\"cellStyleMap\":{},\"email\":\"403413356@qq.com\",\"mobilePhone\":\"13786997720\",\"name\":\"雷龙龙\",\"title\":\"机构办\"},{\"cellStyleMap\":{},\"name\":\"郭峰\",\"title\":\"质控员\"},{\"cellStyleMap\":{},\"name\":\"廖刚\",\"title\":\"药物管理\"}],\"key17\":\"申请流程：http://hnsyzxyy.com/tech/GCPjigou/gongzuozhiyin/2017/0831/1353.html\"},\"pharmacyData\":{\"cellStyleMap\":{}},\"smoAndCroPlan\":{\"cellStyleMap\":{},\"smoAndCroList\":[]}}', NULL, NULL, 1, 1564022111027, 10010, 1583919223758, 0, '', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
