INSERT INTO `t_hospital` VALUES (100561, NULL, NULL, 1005, 1009, '沈阳何氏眼科医院', NULL, '何氏眼科创建于1995年，是一家高科技、国际化、社会责任型眼科集团。\n\n集团已建立了30家眼科医院；26家眼科门诊；48家视光中心,年门诊量突破100万。\n\n集团先后创造了7项全国首例眼科手术，填补了眼科技术空白。\n\n集团在创新发展中，成功探索出以基因、干细胞技术引领的精准医疗模式；以大数据、人工智能设备为依托的远程智慧诊断体系；创建了 “市-县-乡三级防盲扶贫模式”。\n\n集团始终将国际领先技术应用于公益事业，在自主创新技术上不断突破，为健康中国做出贡献。\n\n', '', '400-9090-400', '024-86528900', NULL, 'http://www.hsyk024.com/', NULL, '10100000', '10210000', '10210100', '沈阳市于洪区黄河北大街128号', '', NULL, 0, 0, '{\"ethicsData\":{\"cellStyleMap\":{},\"contacts\":[{\"cellStyleMap\":{},\"name\":\"何向东\",\"telePhone\":\"024-86502018\",\"title\":\"主席/主任\"},{\"cellStyleMap\":{},\"email\":\"hulan@hsyk.com.cn\",\"name\":\"胡兰\",\"telePhone\":\"024-86502018\",\"title\":\"副主任\"},{\"cellStyleMap\":{},\"email\":\"limingyue@hsyk.com.cn\",\"name\":\"李明月\",\"telePhone\":\"024-86502018\",\"title\":\"秘书\"}],\"key1\":\"024-86520800-8701\",\"key10\":\"2个月1次\",\"key2\":\"024-86528900\",\"key3\":\"hecs_iec@sina.com\",\"key8\":\"周一至周五上午8:00-12:00，下午1:00-4:30\",\"key9\":\"第二周周二\"},\"geneticsOfficeData\":{\"cellStyleMap\":{},\"contacts\":[]},\"instructionData\":{\"cellStyleMap\":{},\"contacts\":[{\"cellStyleMap\":{},\"email\":\"gcp@hsyk.com.cn\",\"name\":\"何伟\",\"telePhone\":\"024-26716768\",\"title\":\"机构主任\"},{\"cellStyleMap\":{},\"email\":\"renhuidong@hsyk.com.cn\",\"mobilePhone\":\"18202416812\",\"name\":\"任荟冬\",\"title\":\"办公室主任\"},{\"cellStyleMap\":{},\"email\":\"qiaolin@hsyk.com.cn\",\"name\":\"乔林\",\"telePhone\":\"024-26716768\",\"title\":\"机构秘书\"},{\"cellStyleMap\":{},\"email\":\"wangyue2@hsyk.com.cn\",\"name\":\"王月\",\"telePhone\":\"024-26716768\",\"title\":\"助理\"},{\"cellStyleMap\":{},\"email\":\"jinhaoyan@hsyk.com.cn\",\"name\":\"金皓妍\",\"telePhone\":\"024-26716768\",\"title\":\"助理\"},{\"cellStyleMap\":{},\"name\":\"王俊姝\",\"title\":\"药物管理\"}],\"key2\":\"何氏临研视窗\",\"key4\":\"周一—周五 8:30-12:00 13:00-16:00\",\"key46\":\"辽宁省沈阳市黄河北大街128号沈阳何氏眼科医院门诊楼7楼\",\"key47\":\"024-26716768\",\"key49\":\"024-26716768\",\"key50\":\"gcp@hsyk.co