INSERT INTO `t_hospital` VALUES (100569, NULL, NULL, 1001, 1009, '胜利油田中心医院', 'SHENGLI OILFIELD CENTRAL HOSPITAL', '胜利油田中心医院位于山东省东营市济南路31号，于1964年正式成立，经过50多年的发展，现已成为一所集医疗、教学、科研、康复、预防、保健、急救于一体的三级甲等综合医院。医学影像科为国家临床重点专科建设项目单位，心血管内科为山东省医药卫生重点专业，血液内科为山东省医药卫生重点专业及山东省临床重点专科建设单位，神经内科为山东省临床重点专科，烧伤科为山东省临床重点专科建设单位，中医科为山东省中医药服务能力提升工程重点专科。', '', '0546-8779266', NULL, NULL, 'http://www.slchos.com/', 'slytzyy', '10100000', '10370000', '10370500', '山东省东营市济南路31号', NULL, '257088', 0, 0, '{\"ethicsData\":{\"cellStyleMap\":{},\"contacts\":[],\"key1\":\"0546-8779216\"},\"geneticsOfficeData\":{\"cellStyleMap\":{},\"contacts\":[]},\"instructionData\":{\"cellStyleMap\":{},\"contacts\":[],\"key46\":\"山东省东营市济南路31号\",\"key49\":\"0546-8551382,0546-8770114\",\"key50\":\"zxyygcp01@126.com\"},\"pharmacyData\":{\"cellStyleMap\":{}},\"smoAndCroPlan\":{\"cellStyleMap\":{},\"smoAndCroList\":[]}}', NULL, NULL, 1, 1564022111574, 10010, 1583919342778, 0, '', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
