INSERT INTO `t_hospital` VALUES (100591, NULL, NULL, 1005, 1009, '首都医科大学三博脑科医院', 'Sanbo Brain Hospital Capital Medical University', '首都医科大学三博脑科医院始创于2004年，是由一批国内知名的神经医学专家参与创建的股份制神经专科医院。医院位于香山脚下，占地35亩，总投资1.4亿，床位256张。医院以神经外科为主体，设有8个神经外科病区、1个神经内科病区，拥有麻醉、手术、ICU、影像、病理、脑血管内科、神经眼科、神经电生理室、脑磁图室、神经心理、分子神经病理室等医疗医技科室 ，装备有神经医学高精尖的顶级设备，其中有306通道的脑磁图、长程癫痫定位视频脑电监测系统。是集医疗、教学、科研为一体的学院型医院，隶属于三博脑科医院管理集团。', '', '400-0655-120；010-62856910', NULL, NULL, 'https://www.sbnk.cn/index.html', 'sanbonaoke', '10100000', '10110000', '10110000', '北京市海淀区香山一棵松50号', 'No. 50, Songsong, Xiangshan, Haidian District, Beijing', '100093', 0, 0, '{\"ethicsData\":{\"cellStyleMap\":{},\"contacts\":[{\"cellStyleMap\":{},\"name\":\"王保国\",\"title\":\"伦理主任\"}],\"key1\":\" 010-62856956；010-62856798\",\"key3\":\"sbnk_ec@163.com\",\"key5\":\"北京市海淀区香山路一棵松50号1号楼3层301室\",\"key6\":\"100093\",\"key7\":\"https://www.sbnk.cn/keyan/llwyh/\",\"key8\":\"周一至周五，9:00-16:00\"},\"geneticsOfficeData\":{\"cellStyleMap\":{},\"contacts\":[]},\"instructionData\":{\"cellStyleMap\":{},\"contacts\":[{\"cellStyleMap\":{},\"email\":\"sbnkjigoumishu@163.com\",\"name\":\"栾国明\",\"telePhone\":\"010-62856910\",\"title\":\"机构主任\"},{\"cellStyleMap\":{},\"name\":\"张俊平\",\"telePhone\":\"010-62856910\",\"title\":\"机构副主任\"},{\"cellStyleMap\":{},\"name\":\"王宁\",\"telePhone\":\"010-62856910\",\"title\":\"机构副主任\"},{\"cellStyleMap\":{},\"name\":\"刘菲\",\"telePhone\":\"010-62856910\",\"title\":\"机构副主任\"}],\"key1\":\"https://www.sbnk.cn/keyan/ywlcsyjg/\",\"key4\":\"周三至周五，9:00-16:00\",\"key46\":\"北京市海淀区香山路一棵松50号2号楼4层\",\"key48\":\"100093\",\"key49\":\"010-62856910\",\"key50\":\"sbnkjigoumishu@163.com\"},\"pharmacyData\":{\"cellStyleMap\":{}},\"smoAndCroPlan\":{\"cellStyleMap\":{},\"smoAndCroList\":[]}}', NULL, NULL, 1, 1564022112168, 10010, 1584673793936, 0, '', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
