INSERT INTO `t_hospital` VALUES (100599, NULL, NULL, 1001, 1009, '四平市中心人民医院', 'Siping Central People\'s Hospital', '四平市中心人民医院暨中国医科大学四平医院是一所现代化大型综合性三级甲等医院。始建于1978年。承担着四平地区及吉林、辽宁、黑龙江、内蒙等省份部分区域的医疗服务和急诊急救工作。是国际紧急救援中心网络医院、卫生部数字化试点示范医院、卫生部“优质护理服务示范工程”重点联系医院之一、国家药物临床试验机构、国家级住院医师规范化培训基地、全国脑卒中筛查防治基地、吉林省全科医生临床培养基地、中国医科大学、延边大学、吉林医药学院等6所高等医学院校临床教学医院。2011年挂牌为中国医科大学四平医院。\n\n        医院建筑面积12.86万平方米；开放床位1478张；拥有职工1873人，其中副主任医师以上518人，硕士以上研究生学历336人；设有59个专业学科，年门急诊量67.2万余人次，年住院4.6万人次，年手术量1.6万余例；有各种先进诊疗设备包括大型超导核磁共振系统、体、头部伽玛刀、数字化减影血管造影系统、128层螺旋CT、ECT、各种腔镜、钬激光等；建立了四平地区唯一实行社会化管理与服务的后勤供给基地。\n\n        医院以齐全完善、力量雄厚的专业学科、先进特色的医疗技术以及优质规范的医疗服务而闻名于省内外，有效地发挥着对周边地区医疗技术指导和疑难重危救治中心作用。普外科、血液科是吉林省重点学科，组织工程实验室是省级重点实验室，内分泌科是吉林省特色专科。心血管内科、骨科等15个学科是四平市重点学科;设有设施先进、功能齐备的CCU、ICU病房以及国内一流水平的骨髓移植病房；建立了区域性健康管理中心、胸痛中心、卒中中心等，是四平及周边区域的医疗技术指导中心和急危重症救治中心。', '', '400-656-6706', NULL, NULL, 'http://www.spzxyy.cn/', NULL, '10100000', '10220000', '10220300', '吉林省四平市英雄文化广场繁华街', 'No. 89, n Yingbin Street, Tiexi District, Siping City', NULL, 0, 0, '{\"ethicsData\":{\"cellStyleMap\":{},\"contacts\":[{\"cellStyleMap\":{},\"name\":\"许兆辉\",\"title\":\"主任委员\"},{\"cellStyleMap\":{},\"mobilePhone\":\"18744441643\",\"name\":\"冯博\",\"telePhone\":\"0434-3625127/3624213\",\"title\":\"秘书\"}],\"key1\":\"0434-3648125\",\"key10\":\"每季度例行召开审查会议1次，需要时可以增加审查会议次数\",\"key3\":\"spszxyyllwyh@163.com\",\"key8\":\"每月下旬召开一次\"},\"geneticsOfficeData\":{\"cellStyleMap\":{},\"contacts\":[]},\"instructionData\":{\"cellStyleMap\":{},\"contacts\":[{\"cellStyleMap\":{},\"name\":\"刘海峰\",\"title\":\"机构主任\"},{\"cellStyleMap\":{},\"email\":\"tianshyan@163.com；spzxyytsy@163.com\",\"mobilePhone\":\"1394346790\",\"name\":\"田淑艳\",\"title\":\"机构办副主任/项目接洽\"},{\"cellStyleMap\":{},\"name\":\"金亭含\",\"title\":\"秘书\"},{\"cellStyleMap\":{},\"email\":\"373067602@qq.com\",\"name\":\"孙睿\",\"title\":\"药物/器械管理员\"}],\"key46\":\"四平市铁西区南迎宾街89号门诊地下一层\",\"key49\":\"0434-3625127\",\"key50\":\"tianshyan@163.com\"},\"pharmacyData\":{\"cellStyleMap\":{}},\"smoAndCroPlan\":{\"cellStyleM