INSERT INTO `t_hospital` VALUES (100650, NULL, NULL, 1001, 1009, '威海市中心医院', 'WEIHAI CENTRAL HOSPITAL', '医院始建于1941年，时称东海医院，是中国共产党为适应抗日战争需求在胶东地区率先成立的医院，目前已发展成为一所占地130亩，建筑面积22万余平方米，编制床位1600张，开放床位2355张，拥有职工1800人，集医疗、教学、科研、康复、预防保健等功能于一体的地市级三级甲等综合医院，是青岛大学第八临床医学院、潍坊医学院附属医院，大连医科大学、山东第一医科大学、滨州医学院等医学院校的教学医院，国家住院医师规培基地，国家药物临床试验基地，国家医疗器械临床试验基地，国家爱婴医院，国际紧急救援中心网络医院，山东省博士后创新实践基地，美国华人医师网络医院远程医疗合作中心（国内第二家），美国佛罗里达医院技术合作中心、吴孟超院士工作站、吴孟超肿瘤医学中心、国家级胸痛中心、高级卒中中心、房颤中心、心衰中心（胸痛中心、卒中中心均位列全国百强）。', '', '0631-3806624,0631-3806600', NULL, NULL, 'http://www.whszxyy.com.cn/', 'whswdzxyy', '10100000', '10370000', '10371000', '山东省威海市文登区米山东路西3号', 'Chi, Shandong, Weihai, Wendeng, Mishan E Rd', '264400', 0, 0, '{\"ethicsData\":{\"cellStyleMap\":{},\"contacts\":[{\"cellStyleMap\":{},\"name\":\"张洪胜\",\"title\":\"主任委员\"},{\"cellStyleMap\":{},\"name\":\"杜秀兰\",\"title\":\"副主任委员\"},{\"cellStyleMap\":{},\"email\":\"whzxllwyh@126.com\",\"name\":\"张欢\",\"telePhone\":\"0631-3793362\",\"title\":\"秘书\"}],\"key1\":\"0631-3793362\",\"key3\":\"whzxllwyh@126.com\",\"key5\":\"山东省威海市文登区米山东路西3号\",\"key6\":\"264400\"},\"geneticsOfficeData\":{\"cellStyleMap\":{},\"contacts\":[]},\"instructionData\":{\"cellStyleMap\":{},\"contacts\":[{\"cellStyleMap\":{},\"name\":\"王江军\",\"title\":\"联系人\"},{\"cellStyleMap\":{},\"name\":\"李莉\",\"title\":\"联系人\"}],\"key46\":\"山东省威海市文登区米山东路西3号\",\"key48\":\"264400\",\"key49\":\"0631-3917508,0631-3806810\",\"key50\":\"whszxyygcp@126.com\"},\"pharmacyData\":{\"cellStyleMap\":{}},\"smoAndCroPlan\":{\"cellStyleMap\":{},\"smoAndCroList\":[]}}', ' 12371000494506449X', '丛海波 ', 1, 1564022113812, 10010, 1583919339601, 0, '', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
