INSERT INTO `t_hospital` VALUES (100671, NULL, NULL, 1005, 1021, '武汉爱尔眼科医院', 'Zhejiang Rui\'an People\'s Hospital', '武汉爱尔眼科医院是2003年经湖北省卫计委批准成立的一所集医疗、教学、科研和防盲治盲为一体的大型三级专业眼科医院。医院先后被认定为国家药物临床试验基地、湖北省临床重点专科、武汉市市级临床重点专科、湖北省住院医师规范化培训专业基地、中南大学爱尔眼科学院教学基地、湖北省贫困白内障复明工程项目实施指定医院、湖北省教育厅中小学生视力健康指定宣教机构。', '', '027-68893999', '027-68893999', NULL, 'http://www.eye027.com/', 'wheye027', '10100000', '10420000', '10420100', '武汉市武昌区中山路481号（武昌大东门立交桥旁）', '481 Zhongshan Rd, Wuchang Qu, Wuhan Shi, Hubei Sheng, Chi', NULL, 0, 0, '{\"ethicsData\":{\"cellStyleMap\":{},\"contacts\":[{\"cellStyleMap\":{},\"name\":\"王勇\",\"title\":\"主任委员\"},{\"cellStyleMap\":{},\"name\":\"张庆\",\"title\":\"副主任委员\"},{\"cellStyleMap\":{},\"name\":\"吴尚操\",\"title\":\"伦理委员\"},{\"cellStyleMap\":{},\"name\":\"李朝辉\",\"title\":\"伦理委员\"},{\"cellStyleMap\":{},\"name\":\"陈馨\",\"title\":\"伦理委员\"},{\"cellStyleMap\":{},\"name\":\"詹汉英\",\"title\":\"伦理委员\"},{\"cellStyleMap\":{},\"name\":\"游友安\",\"title\":\"伦理委员\"},{\"cellStyleMap\":{},\"name\":\"汪莲春\",\"title\":\"伦理委员\"},{\"cellStyleMap\":{},\"name\":\"龙伶\",\"title\":\"伦理委员\"},{\"cellStyleMap\":{},\"name\":\"计媛媛\",\"title\":\"伦理秘书\"}],\"key1\":\" 027-68893991\",\"key10\":\" 每季度至少一次\",\"key2\":\" 027-68893991\",\"key3\":\"eye027whkjb@163.com\\n923288628@qq.com\",\"key5\":\"武汉市中山路481号武汉爱尔眼科医院门诊2楼\",\"key8\":\"周一至周五\",\"key9\":\"依项目而定\"},\"geneticsOfficeData\":{\"cellStyleMap\":{},\"contacts\":[]},\"instructionData\":{\"cellStyleMap\":{},\"contacts\":[{\"cellStyleMap\":{},\"name\":\" 吴建华\",\"title\":\"机构办公室主任\"},{\"cellStyleMap\":{},\"name\":\"陈馨\",\"title\":\"机构办公室主任\"},{\"cellStyleMap\":{},\"name\":\" 张艳慧\",\"title\":\"机构秘书\"},{\"cellStyleMap\":{},\"name\":\"王为\",\"title\":\"机构质控\"},{\"cellStyleMap\":{},\"name\":\"谭湄萍\",\"title\":\"机构药物管理员\"},{\"cellStyleMap\":{},\"name\":\"王丹\",\"position\":\"资料管理员\"}],\"key1\":\"http://www.eye027.com/aier/yaowu.htm\",\"key4\":\"whaiergcp@163.com\",\"key46\":\"027-68893766 \",\"key48\":\"Ⅱ、Ⅲ、Ⅳ期药物临床试验、上市后再评价、医疗器械临床试验、体外诊断试剂临床试验\",\"key5\":\"是\",\"