INSERT INTO `t_hospital` VALUES (100678, NULL, NULL, 1005, 1009, '武汉市精神卫生中心', 'Weihai Wendeng Central Hospital', '武汉市精神卫生中心（武汉市心理医院）始建于1956年，1997年获批成为湖北省首家“三级甲等”精神专科医院，2018年成功复评。历经六十余年的建设发展，现已成为融医疗、教学、科研、预防、康复于一体的现代化医院。同时还是华中科技大学同济医学院附属武汉精神卫生中心暨第九临床学院、中国地质大学附属武汉心理医院、武汉市精神残疾康复技术指导中心等。先后获得“第一批国家特色医院文化医院”、“湖北省首批医务社工试点医院”、“全市百佳法治建设示范单位”、“市级文明单位”等称号。\n\n中心现有六角亭、二七两个院区，编制床位950张,年门急诊量30余万人次，出院患者数达万余人次。拥有3.0T医用磁共振成像系统等一批诊疗科研设备。连续举办具有国际影响力的中美、中法等精神分析培训项目，积极承办世界卫生组织（WHO）、国际精神分析协会（IPA）等国际组织的学术交流活动，获广泛关注，影响深远。', '', '85836685（六角亭院区）', NULL, NULL, 'http://www.chinapsy.com/', NULL, '10100000', '10420000', '10420100', '武汉市硚口区游艺路93号', 'Youyi Rd, Wan Song Yuan, Qiaokou Qu, Wuhan Shi, Hubei Sheng, Chi', '430022', 0, 0, '{\"ethicsData\":{\"cellStyleMap\":{},\"contacts\":[{\"cellStyleMap\":{},\"email\":\"85398645@qq.com\",\"name\":\"徐艳芩\"}],\"key1\":\"027-59372344；027-59372347\"},\"geneticsOfficeData\":{\"cellStyleMap\":{},\"contacts\":[]},\"instructionData\":{\"cellStyleMap\":{},\"contacts\":[{\"cellStyleMap\":{},\"email\":\"fangmaosheng2006@163.com\",\"name\":\"房茂胜\",\"position\":\"机构办主任\"},{\"cellStyleMap\":{},\"email\":\"376642109@qq.com\",\"name\":\"徐晓津\",\"position\":\"机构办秘书\"}],\"key46\":\"武汉市硚口区游艺路93号\",\"key49\":\"027-82281635\"},\"pharmacyData\":{\"cellStyleMap\":{}},\"smoAndCroPlan\":{\"cellStyleMap\":{},\"smoAndCroList\":[]}}', NULL, NULL, 1, 1564022114577, 10010, 1583919196285, 0, '', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
