INSERT INTO `t_hospital` VALUES (100682, NULL, NULL, 1005, 1009, '武汉亚洲心脏病医院', 'Weihai Wendeng Central Hospital', '武汉亚洲心脏病医院成立于1999年11月11日，是一家三级甲等心脏病专科医院。现任院长为陶凉教授。医院聚集了国内优秀的心血管疾病诊疗专家，能够成熟实施各类心脏内、外科手术。\n\n医院建筑面积4.6万平方米，开放床位750张，截至2019年底，已接诊来自全国31个省、市、自治区的心脏病患者达445万余人次，并成功实施各类心脏手术33.4万余例。\n\n其中，医院心外科累计完成各类心脏手术6.5万余例，率先开展的二十余项手术达到国内一流水平，连续十六年位居全国前三位、湖北省首位。心内科/介入中心完成各类心血管介入检查和手术达26.9万余例，年手术量超2.9万例，位居全国前三位、湖北省首位。医院还承担了多项国家十五攻关课题和国内外科研项目研究。', '', '400-888-4027', '027-85412677', NULL, 'https://www.wahh.com.cn/', NULL, '10100000', '10420000', '10420100', '湖北省武汉市京汉大道753号', ' 753 Jinghan Ave, Jiang Han Lu Shang Quan, Jianghan Qu, Wuhan Shi, Hubei Sheng, Chi', '430022', 0, 0, '{\"ethicsData\":{\"cellStyleMap\":{},\"contacts\":[],\"key1\":\"027-65796689\",\"key5\":\"湖北省武汉市京汉大道753号6楼\",\"key8\":\"周一至周五 上午8:30-12:00 下午14:00-17:30\"},\"geneticsOfficeData\":{\"cellStyleMap\":{},\"contacts\":[]},\"instructionData\":{\"cellStyleMap\":{},\"contacts\":[{\"cellStyleMap\":{},\"email\":\"zhouxin2007328@163.com\",\"name\":\"周欣\",\"position\":\"机构办主任\",\"telePhone\":\"027-65796888-0308\"}],\"key4\":\"周一至周五 上午8:30-12:00 下午14:00-17:30\",\"key46\":\"湖北省武汉市京汉大道753号武汉亚洲心脏病医院6楼科教部\",\"key47\":\"027-65796809\",\"key49\":\"027-65796809\",\"key50\":\"wahh_gcp@163.com\"},\"pharmacyData\":{\"cellStyleMap\":{}},\"smoAndCroPlan\":{\"cellStyleMap\":{},\"smoAndCroList\":[]}}', NULL, NULL, 1, 1564022114685, 10010, 1583919198132, 0, '', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
