INSERT INTO `t_hospital` VALUES (100688, NULL, NULL, 1001, 1009, '西安交通大学第二附属医院', 'The 3st Affiliated Hospital', '西安交通大学第二附属医院是国家卫生健康委员会、教育部直属的一所集医疗、教学、科研、预防保健为一体的现代化大型综合医院，国家首批三级甲等医院。医院源起1912年由国家教育部创办的中国第一所国立医学院校——国立北京医学专门学校、国立北平大学医学院附属医院，是中国第一所国立大学附属医院。自1937年在抗战烽火中为民族大义迁陕重建，81年来，兴医强国，艰苦奋斗，精勤育人，救死扶伤，为西北医学高等教育和现代医学科学的创立、发展做出了卓越贡献。', '', '029-86691217', NULL, NULL, 'http://2yuan.xjtu.edu.cn/', NULL, '10100000', '10610000', '10610100', '西安市西五路157号', '', NULL, 0, 0, '{\"ethicsData\":{\"cellStyleMap\":{},\"contacts\":[],\"key1\":\"029-87678326\"},\"geneticsOfficeData\":{\"cellStyleMap\":{},\"contacts\":[]},\"instructionData\":{\"cellStyleMap\":{},\"contacts\":[{\"cellStyleMap\":{},\"telePhone\":\"029-87678325\",\"title\":\"科研部\"}],\"key46\":\"陕西省西安市西五路157号\",\"key47\":\"029-87678326\",\"key49\":\"029-87678326\",\"key50\":\"jigouban326@163.com\"},\"pharmacyData\":{\"cellStyleMap\":{}},\"smoAndCroPlan\":{\"cellStyleMap\":{},\"smoAndCroList\":[]}}', NULL, NULL, 1, 1564022114866, 10010, 1583919371750, 0, '', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `t_hospital` VALUES (100689, NULL, NULL, 1001, 1009, '西安交通大学第一附属医院', 'Eye Hospital, WMU Zhejiang Eye Hospital', '西安交通大学第一附属医院的发展史与国家的发展、民族的命运一脉相承，1937年因抗日战争爆发，北平大学医学院部分师生西迁组建了西安临时大学医学院，附属医院随之诞生，存续了中国最早国立西医高等教育的薪火。1956年一分为二，搬迁现址者命名为西安医学院第一附属医院。数十载奋斗不息，医院现已发展为集医疗、教学、科研、康复、预防保健为一体的国家卫计委委管大型综合性三级甲等医院，是全国首批暨陕西省首家“三级甲等医院”、全国首批“百佳医院”，是国家卫计委国际紧急救援中心网络医院，陕西省涉外医疗单位，陕西省卫生应急救援队单位和省级保健基地，是首批新农合省级医疗定点机构、省市城镇职工、居民、商业医保定点机构。建院以来，医院坚持以精湛的医疗技术、严谨的治学态度、求实的科研作风和优秀的服务品质开展医疗、教学和科研工作，各项事业不断发展。医院占地面积 14.8万平方米，建筑面积20.5万平方米，实际开放床位2560张，医疗设施完善，设备先进，医疗技术和服务水平在西北地区居领先地位。', '', '0913--5169036', NULL, NULL, 'http://www.dyyy.xjtu.edu.cn/', NULL, '10100000', '10610000', '10610100', '陕西省西安市雁塔西路277号 ', '277 Yanta W Rd, Dian Zi Cheng Shang Quan, Yanta Qu, Xian Shi, Shaanxi Sheng, Chi', '710061', 0, 0, '{\"ethicsData\":{\"cellStyleMap\":{},\"contacts\":[{\"cellStyleMap\":{},\"name\":\"杨岚\",\"title\":\"主任委员\"},{\"cellStyleMap\":{},\"name\":\"董亚琳\",\"title\":\"副主任委员\"},{\"cellStyleMap\":{},\"email\":\"Zhangcaixia313@sina.com\",\"name\":\"张彩霞\",\"title\":\"秘书\"}],\"key1\":\"029-85323473\",\"key10\":\"每月一次。需要时可增加次数\",\"key17\":\"伦理审查费3300 \",\"key2\":\"029-85323473\",\"key21\":\"1100\",\"key3\":\"xjyfyllh@163.com\",\"key5\":\"西安市雁塔西路277号科教楼1楼0108\",\"key8\":\"每周工作日周一到周五\",\"key9\":\"每月中下旬\"},\"geneticsOfficeData\":{\"cellStyleMap\":{},\"contacts\":[]},\"instructionData\":{\"cellStyleMap\":{},\"contacts\":[{\"cellStyleMap\":{},\"name\":\"袁祖贻\",\"telePhone\":\"029-85323819\",\"title\":\"主任\"},{\"cellStyleMap\":{},\"email\":\"Lumy3215@163.com\",\"name\":\"陆明莹 科教楼1楼0107\",\"telePhone\":\"029-85324783\",\"title\":\"机构办公室主任\"},{\"cellStyleMap\":{},\"name\":\"张田香\",\"telePhone\":\"029-85324094\",\"title\":\"机构秘书\"}],\"key4\":\"周一至周五工作时间\",\"key46\":\"西安市雁塔西路277号 科教楼1楼0102\",\"key47\":\"029-85324094\",\"key49\":\"029-85324094\",\"key50\":\"xayyyljd@163.com\"},\"pharmacyData\":{\"cellStyleMap\":{}},\"smoAndCroPlan\":{\"cellStyleMap\":{},\"smoAndCroList\":[]}}', NULL, NULL, 1, 1564022114892, 10010, 15839