INSERT INTO `t_hospital` VALUES (100694, NULL, NULL, 1001, 1009, '西安医学院第一附属医院', 'Weihai Wendeng Central Hospital', '西安医学院第一附属医院是国家新一轮陕西省首家评审通过的综合性三级甲等医院，创建于1951年，历经69年的建设和发展，已成为一所集医疗、教学、科研、预防保健、康复与急救为一体具有较强救治能力、较高科研水平和国际交流能力的综合性教学医院。近年来医院注重管理创新，将精细化管理有效应用在医疗质量管理和医院服务管理中，致力于医疗、教学、科研及管理四轮协同发展。导入卓越绩效管理模式，全院开展5+1S精益管理，取得了显著成效。已逐步发展为陕西省新兴的品牌医院,在2016年度区域医院综合排行榜（复旦版）跻身西北20强，呼吸内科和神经内科等4个学科获得提名；在科研和学术方面，已跃升为西北15强。医院以院本部为核心，同时托管了沣东新城纺织医院（西安医学院第一附属医院沣东医院）、莲湖区土门社区卫生服务中心。医院先后成立了沣东新城和莲湖区公立医院两个医疗联合体,成员单位涵盖1家三级医院，8家二级医院，10家一级医院，1所养老院，40余个社区卫生服务中心和卫生院，三家合作单位。', '', '029-84277356', NULL, 'xyyfyyb@163.com', 'http://www.xyfy.com.cn/', NULL, '10100000', '10610000', '10610100', '陕西省西安市莲湖区丰镐西路48号', '48 Fenghao W Rd, Tu Men Shang Quan, Lianhu Qu, Xian Shi, Shaanxi Sheng, Chi', NULL, 0, 0, '{\"ethicsData\":{\"cellStyleMap\":{},\"contacts\":[{\"cellStyleMap\":{},\"name\":\"张玉荣\"}],\"key1\":\"029-84230225\"},\"geneticsOfficeData\":{\"cellStyleMap\":{},\"contacts\":[]},\"instructionData\":{\"cellStyleMap\":{},\"contacts\":[{\"cellStyleMap\":{},\"name\":\"李亚军\",\"title\":\"机构负责人\"},{\"cellStyleMap\":{},\"name\":\"王莉\",\"title\":\"机构办公室主任\"}]},\"pharmacyData\":{\"cellStyleMap\":{}},\"smoAndCroPlan\":{\"cellStyleMap\":{},\"smoAndCroList\":[]}}', NULL, NULL, 1, 1564022115026, 10010, 1583919375249, 0, '', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
