INSERT INTO `t_hospital` VALUES (100729, NULL, NULL, 1001, 1009, '延安大学咸阳医院', NULL, '延安大学咸阳医院（原中铁二十局中心医院）前身是1949年在山东胶东半岛成立的中国人民解放军战地医院，1951年参加抗美援朝战争，1984年集体转业，1987年迁驻咸阳，2013年更名为延安大学咸阳医院。经过近七十年的建设，已发展成为一所集医疗、教学、科研、预防、保健、康复为一体的大型三级甲等综合性医院，是延安大学医学院第三附属医院，国家药物临床试验机构，国家医疗器械临床试验机构，陕西省住院医师规范化培训基地，延安大学研究生联合培养基地，中国高级卒中中心，中国胸痛中心，下辖“五个分院，一个中心”，综合实力居于地区前列，心脑血管重点专科省内外知名。', '', '029-33766666', NULL, 'yzxx20jyy@163.com', 'www.20jyy.com', NULL, '10100000', '10610000', '10610400', '中国咸阳文林路中段38号 ', NULL, NULL, 0, 0, '{\"ethicsData\":{\"cellStyleMap\":{},\"contacts\":[{\"cellStyleMap\":{},\"name\":\"张海涛\",\"title\":\"主任委员\"},{\"cellStyleMap\":{},\"name\":\"韩晓芳\",\"title\":\"副主任委员\"},{\"cellStyleMap\":{},\"email\":\"1183817859@qq.com\",\"name\":\"白晓东\",\"title\":\"秘书\"}],\"key1\":\"029-33779387\",\"key10\":\"根据项目审查情况确定\",\"key2\":\"029-33779387\",\"key3\":\"ydxyyxll@163.com\",\"key5\":\"陕西省咸阳市渭城区文林路中段38号延安大学咸阳医院心脏楼3楼医学伦理委员会办公室\",\"key8\":\"周一至周五：上午8:00-11:45；下午1:30-5:00；周六：上午8:00-11:45；节假日\",\"key9\":\"根据项目审查情况确定\"},\"geneticsOfficeData\":{\"cellStyleMap\":{},\"contacts\":[]},\"instructionData\":{\"cellStyleMap\":{},\"contacts\":[{\"cellStyleMap\":{},\"name\":\"张群\",\"title\":\"机构主任\"},{\"cellStyleMap\":{},\"name\":\"康彦文\",\"title\":\"机构副主任\"},{\"cellStyleMap\":{},\"name\":\"王丙聚\",\"title\":\"机构办公室主任\"},{\"cellStyleMap\":{},\"name\":\"刘琳洁\",\"title\":\"机构办公室秘书\"}],\"key4\":\"周一至周五：上午8:00-11:45；下午1:30-5:00；周六：上午8:00-11:45；节假日休息\",\"key46\":\"陕西省咸阳市渭城区文林路中段38号延安大学咸阳医院心脏楼3楼国家药物临床试验机构办公室\",\"key47\":\"029-33779387\",\"key49\":\"029-33779387\",\"key50\":\"ydxygcp@163.com\"},\"pharmacyData\":{\"cellStyleMap\":{}},\"smoAndCroPlan\":{\"cellStyleMap\":{},\"smoAndCroList\":[]}}', NULL, NULL, 1, 1564022115970, 10010, 1583919375524, 0, '', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
