INSERT INTO `t_hospital` VALUES (100741, NULL, NULL, 1001, 1010, '应急总医院', 'Emergency General Hospital', '应急总医院，前身为煤炭总医院，是一所集医疗、教学、科研、预防为一体的三级综合性医院。是应急管理部所属在京事业单位，是北京市涉外医疗医院、中国协和医科大学的教学医院、华北理工大学非隶属附属医院及国际救援中心(SOS)定点合作医院。\n\n医院于1987年经原国家计委批准兴建。1993年正式落成开诊。1998年，由原北京市卫生局批复为三级综合医院，1999年，通过评审确立为三级合格医院。随着历次政府机构改革，先后由煤炭工业部、国家煤炭工业局、国家安全生产监督管理局、国家安全生产监督管理总局、应急管理部领导。\n\n医院编制床位515张，现有职工972人。其中高级职称198人，博士82人，硕士170人。设有38个医疗、医技科室，开展了80余个专业的诊断和治疗工作。呼吸内科、肿瘤内科专业具有较强优势，气管镜介入治疗走在专科医疗技术的前沿。心内科、骨科、普外科、职业病科、中医科、影像科等学科具有一定特色和优势', '', '(010)87935261', '010-64632579', 'mtyyrsc@163.com', 'www.mtzyy.com.cn', 'YJZYY_official', '10100000', '10110000', '10110000', '北京朝阳区西坝河南里29号', '', '100028', 0, 0, '{\"ethicsData\":{\"cellStyleMap\":{},\"contacts\":[{\"cellStyleMap\":{},\"name\":\"周正\",\"position\":\"知名专家 主任医师 博士 硕士研究生导师\",\"title\":\"主任委员\"},{\"cellStyleMap\":{},\"name\":\"张艳蕾\",\"title\":\"办公室主任\"},{\"cellStyleMap\":{},\"name\":\"祖洁琛\",\"title\":\"秘书\"}],\"key1\":\"010--87935529，010-64667755-2377\",\"key3\":\"mtllwyh@163.com\",\"key5\":\"应急总医院新行政楼606房间\"},\"geneticsOfficeData\":{\"cellStyleMap\":{},\"contacts\":[]},\"instructionData\":{\"cellStyleMap\":{},\"contacts\":[{\"cellStyleMap\":{},\"email\":\"lijing0127@sina.com\",\"name\":\"李静\",\"telePhone\":\"010-64628696；010-64662127\",\"title\":\"机构副主任/机构办主任\"}],\"key1\":\"http://www.mtzyy.com.cn/Html/Departments/Main/Index_157.html\",\"key4\":\"周一至周五\",\"key46\":\"北京市朝阳区西坝河南里29号医院综合楼110\",\"key49\":\"010-64628696；010-64662127\",\"key50\":\"lijing0127@sina.com\"},\"pharmacyData\":{\"cellStyleMap\":{}},\"smoAndCroPlan\":{\"cellStyleMap\":{},\"smoAndCroList\":[]}}', NULL, NULL, 1, 1564022116304, 10010, 1584673795015, 0, '', 100043, '北京医院', NULL, NULL, NULL, NULL, NULL);
