INSERT INTO `t_hospital` VALUES (100749, NULL, NULL, 1001, 1009, '云南省第二人民医院', NULL, '云南省第二人民医院暨云南省红十字会医院始建于1928年，原名万国红十字会昆明分会医院，1938年抗战期间作为南迁来昆的上海同济大学医学部教学医院。医院秉承“人道、博爱、奉献”的传统，以“关爱生命，患者至上”为医院的宗旨和核心价值追求，定位于“服务优质、特色鲜明、专长突出、综合全面”，历经近90年的风雨历程，现已成为一所集医疗、教学、科研、健康保健为一体的省级现代化三级甲等综合医院。\n\n医院占地面积52亩，开放病床1500张，现有教医护职工2200余人，高级卫生技术人员200余人。医院学科设置齐全，共有52个临床医技科室，拥有先进的医疗仪器设备（包括省内独家的320排640层动态容积CT、全飞秒激光“visuMax”等）。', '', '0871-65156650', NULL, NULL, 'http://www.ynshhyy.com/', 'yns2yy', '10100000', '10530000', '10530100', '云南省昆明市青年路176号', '', '650021', 0, 0, '{\"ethicsData\":{\"cellStyleMap\":{},\"contacts\":[],\"key1\":\"0871-65122299\"},\"geneticsOfficeData\":{\"cellStyleMap\":{},\"contacts\":[]},\"instructionData\":{\"cellStyleMap\":{},\"contacts\":[{\"cellStyleMap\":{},\"name\":\"王老师\",\"title\":\"联络人\"}],\"key49\":\"0871-65156650\",\"key50\":\"yns2yygcp@sina.com\"},\"pharmacyData\":{\"cellStyleMap\":{}},\"smoAndCroPlan\":{\"cellStyleMap\":{},\"smoAndCroList\":[]}}', NULL, NULL, 1, 1564022116519, 10010, 1583919464355, 0, '', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `t_hospital` VALUES (100750, NULL, NULL, 1001, 1009, '云南省第一人民医院', 'The First People\'s Hospital of Yunnan Province', '云南省第一人民医院（又名：昆华医院）位于春城昆明市中心著名地标金马碧鸡坊旁，筹建于1932年，1939年正式开业，是云南省最早的中国人自办省立医院。现已成为云南省疑难危重疾病的救治中心，是集医疗、科研、教学和紧急救援为一体的大型三级甲等综合医院，是昆明理工大学附属医院、国际救援定点医院、国家紧急医学救援队建设单位。', '', '0871-63639921,63645556', '0871-63627731', 'khyy157@126.com', 'http://www.ypfph.com/', 'khwxgzpt', '10100000', '10530000', '10530100', '云南省昆明市西山区金碧路157号', '', '650034', 0, 0, '{\"ethicsData\":{\"cellStyleMap\":{},\"contacts\":[{\"cellStyleMap\":{},\"name\":\"江晓云\",\"title\":\"主席/主任\"},{\"cellStyleMap\":{},\"email\":\"564267128@qq.com\",\"name\":\"张志清\",\"title\":\"秘书\"}],\"key1\":\"0871-63663167\",\"key10\":\"每月一次\",\"key2\":\"0871-63663167\",\"key3\":\"khll_yn@163.com\",\"key5\":\"云南省昆明市西山区金碧路157号云南省第一人民医院二号门诊13楼药物临床试验临床委员会办公室\\n\",\"key6\":\"650034\",\"key8\":\"上午：8：00－12：00下午：14：00－18：00\\n\"},\"geneticsOfficeData\":{\"cellStyleMap\":{},\"contacts\":[]},\"instructionData\":{\"cellStyleMap\":{},\"contacts\":[{\"cellStyleMap\":{},\"name\":\"王平\",\"title\":\"主任\"},{\"cellStyleMap\":{},\"email\":\"caowei@medmail.com.cn\",\"name\":\"曹玮\",\"title\":\"办公室主任\"},{\"cellStyleMap\":{},\"email\":\"13700692312@139.com\",\"name\":\"洪敏\",\"title\":\"机构秘书\"},{\"cellStyleMap\":{},\"email\":\"104981149@qq.com\",\"name\":\"刘佳\",\"title\":\"质控员\"},{\"cellStyleMap\":{},\"email\":\"465391503@qq.com\",\"name\":\"卫薇\",\"title\":\"药品管理员\"}],\"key4\":\"上午：8：00－12：00下午：14：00－18：00\",\"key46\":\"云南省昆明市西山区金碧路157号云南省第一人民医院二号门诊13楼药物临床试验机构办公室\",\"key47\":\"0871-63662167\",\"key48\":\"650034\",\"key49\":\"0871-63662173\",\"key50\":\"khjg_yn@163.com\"},\"pharmacyData\":{\"cellStyleMap\":{}},\"smoAndCroPlan\":{\"cellStyleMap\":{},\"smoAndCroList\":[]}}', '12530000431201816X', '蒋立虹 ', 1, 1564022116545, 10010, 1583919463307, 0, '', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `t_hospital` VALUES (100751, NULL, NULL, 1002, 1009, '云南省中医医院', 'Yunn Provincial Hospital of Traditiol Chinese Medicine', '云南省中医医院（云南中医学院第一附属医院）设一院两址，光华院区位于云南省昆明市光华街120号，滇池院区位于云南省昆明市滇池度假区华晨路1号。医院创建于1947年，是一所三级中医医院，也是集云南省102家省、州（市）、县（区）中医院和相关单位为一体的云南省中医医疗集团总医院。', '', '0871-63631649', NULL, 'yntcm_hopxc@126.com', 'http://www.yn-tcm-hospital.com/', 'ynszyyy', '10100000', '10530000', '10530100', '云南省昆明市光华街120号', '', '650021', 0, 0, '{\"ethicsData\":{\"cellStyleMap\":{},\"contacts\":[{\"cellStyleMap\":{},\"name\":\"李琦\",\"title\":\"医药委员会主任\"},{\"cellStyleMap\":{},\"name\":\"方琴\",\"title\":\"医学院专职秘书\"},{\"cellStyleMap\":{},\"name\":\"张春和\",\"title\":\"医学兼职秘书\"},{\"cellStyleMap\":{},\"name\":\"刘晓萍\",\"title\":\"医学院秘书\"}],\"key1\":\"0871-63625561\",\"key3\":\"ynszyyylunli@126.com\"},\"geneticsOfficeData\":{\"cellStyleMap\":{},\"contacts\":[]},\"instructionData\":{\"cellStyleMap\":{},\"contacts\":[{\"cellStyleMap\":{},\"name\":\"温伟波\",\"title\":\"机构主任\"},{\"cellStyleMap\":{},\"name\":\"叶建州\",\"title\":\"机构副主任\"},{\"cellStyleMap\":{},\"name\":\"童晓云\",\"title\":\"办公室主任\"},{\"cellStyleMap\":{},\"name\":\"曾婷婷\",\"title\":\"机构秘书\"}],\"key4\":\"周一至周五\",\"key46\":\"昆明市光华街120号云南省中医医院2号楼210室\",\"key47\":\"0871-63623713\",\"key48\":\"650021\",\"key49\":\"0871-63623713,0871-63632023\",\"key50\":\"ynzygcp1@163.com\"},\"pharmacyData\":{\"cellStyleMap\":{}},\"smoAndCroPlan\":{\"cellStyleMap\":{},\"smoAndCroList\":[]}}', '12530000431201859B', '温伟波 ', 1, 1564022116572, 10010, 1583919463307, 0, '', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `t_hospital` VALUES (100752, NULL, NULL, 1005, 1009, '云南省肿瘤医院', 'Yunnan Cancer Hospital', '云南省肿瘤医院 昆明医科大学第三附属医院 云南省癌症中心 昆明医科大学临床肿瘤学院始建于1984年、1992年正式开诊，是云南省唯一集医疗、教学、科研、预防于一体的三级甲等肿瘤专科医院，承担着全省肿瘤防治研究、人才培养及肿瘤学术交流任务。是云南省肿瘤诊疗质量控制中心，国家卫生健康委肺癌、食管癌、大肠癌等肿瘤早诊早治基地，云南省肿瘤继续教育基地，云南省肺癌国际合作示范基地，云南区域性高发肺癌研究协同创新中心、分子肿瘤学国家重点实验室云南省肿瘤医院转化医学中心和国家药物临床试验（肿瘤学）机构。教育部国际合作联合实验室“分子诊断中心”作为云南省唯一一家入选单位，被评为欧洲病理质控中心、国家卫计委病理质控评价中心（PQCC）分子病理示范实验室。', '', '86-0871-68185656 68181942', NULL, NULL, 'http://www.ynszlyy.com/', 'yns_zlyy', '10100000', '10530000', '10530100', '云南省昆明市西山区昆州路519号', '', '650118', 0, 0, '{\"ethicsData\":{\"cellStyleMap\":{},\"contacts\":[{\"cellStyleMap\":{},\"name\":\"吴红明\",\"title\":\"主任委员\"},{\"cellStyleMap\":{},\"name\":\"刘志敏\",\"title\":\"副主任委员\"},{\"cellStyleMap\":{},\"name\":\"叶联华\",\"title\":\"秘书\"},{\"cellStyleMap\":{},\"name\":\"许玉玲\",\"title\":\"秘书\"}],\"key1\":\"0871-68179625,0871-68185656\",\"key3\":\"ynzlyyll@163.com\",\"key5\":\"云南省昆明市昆州路519号云南省肿瘤医院后勤楼307室\",\"key6\":\"650118\"},\"geneticsOfficeData\":{\"cellStyleMap\":{},\"contacts\":[]},\"instructionData\":{\"cellStyleMap\":{},\"contacts\":[{\"cellStyleMap\":{},\"email\":\"chenxuemeikmyxy@163.com\",\"name\":\"陈雪梅\",\"title\":\"机构办主任\"}],\"key4\":\"周一至周五（8：00〜12：00,14：00〜18：00）\",\"key46\":\"云南省昆明市昆州路519号云南省肿瘤医院技教楼二楼GCP办公室\",\"key48\":\"650118\",\"key49\":\"0871-68103376\",\"key50\":\"zhongliugcp@aliyun.com\"},\"pharmacyData\":{\"cellStyleMap\":{}},\"smoAndCroPlan\":{\"cellStyleMap\":{},\"smoAndCroList\":[]}}', '12530000431201824Q', '黄云超 ', 1, 1564022116599, 10010, 1583919463347, 0, '', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
