INSERT INTO `t_hospital` VALUES (100766, NULL, NULL, 1001, 1009, '长沙市中心医院', 'Changsha Central Hospital', '南华大学附属长沙中心医院是一所集医疗、急救、保健、康复、科研、教学为一体的三级甲等综合医院，设有本部和北院，于2019年1月19日由原长沙市中心医院授牌成为南华大学直属型附属医院。医院本部位于长沙市韶山南路与二环线新中路立交桥交界的要冲地带；北院位于长沙市湘春路长春巷2号。医院占地面积达190亩（其中北院15亩），建筑面积13.03万平方米，资产总值达8.21亿元人民币。配备有直线加速器、陀螺刀、3.0T及1.5T磁共振、64排螺旋CT、数字减影心血管造影仪（DSA）、单光子计算机断层扫描机（ECT）、直接数字化X光机（DR）、四维彩色超声、颈动脉血管超声、超声上消化道镜系统、双脉冲钬激光碎石系统、全套电子内镜系统、高速全自动生化分析仪、珊顿全套病理设备等先进的诊疗设备。', '', '86-0731-84313697', NULL, NULL, 'http://www.csszxyy.com/', NULL, '10100000', '10430000', '10430100', '湖南省长沙市韶山南路161号', NULL, NULL, 0, 0, '{\"ethicsData\":{\"cellStyleMap\":{},\"contacts\":[{\"cellStyleMap\":{},\"name\":\"彭骅\",\"title\":\"主任委员\"},{\"cellStyleMap\":{},\"mobilePhone\":\"15197181559\",\"name\":\"吴亚娟\",\"title\":\"伦理办公室\"},{\"cellStyleMap\":{},\"mobilePhone\":\"13786111245\",\"name\":\"毛艳梅\",\"title\":\"伦理办公室\"}],\"key1\":\"0731-85667845\",\"key10\":\"每月1次\",\"key17\":\"会议审查：5000元（药物），4000元（器械）不含税费；\",\"key2\":\"0731-85668157\",\"key21\":\"快速审查：1000元 不含税费\",\"key3\":\"csszxyyll@163.com\",\"key5\":\"湖南省长沙市雨花区韶山南路161号 长沙市中心医院老药学楼二楼205室\",\"key8\":\"工作日（8:00-12:00/14:30-17:30）\",\"key9\":\"视项目情况而定\"},\"geneticsOfficeData\":{\"cellStyleMap\":{},\"contacts\":[]},\"instructionData\":{\"cellStyleMap\":{},\"contacts\":[],\"key4\":\"工作日 8:00-12:00，14:30-17:30\",\"key46\":\"长沙市雨花区韶山南路161号 医院药学楼三楼311室\",\"key47\":\"0731-85667872\",\"key49\":\"0731-85667872\",\"key50\":\"csszxyygcp@126.com\"},\"pharmacyData\":{\"cellStyleMap\":{}},\"smoAndCroPlan\":{\"cellStyleMap\":{},\"smoAndCroList\":[]}}', NULL, NULL, 1, 1564022116989, 10010, 1583919227058, 0, '', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
